export class DatabaseStorage implements IStorage {
  // Transactions with asset movement logic
  async createTransactionWithAssetMovement(userId: string, transaction: InsertTransaction): Promise<Transaction> {
    // P2P ê±°ë˜ëŠ” ëŒ€ê¸° ìƒíƒœë¡œ ìƒì„± (ì‹¤ì œ ì…ê¸ˆ í™•ì¸ í›„ confirmedë¡œ ë³€ê²½)
    const transactionData = {
      ...transaction,
      status: (transaction.type === 'binance_p2p' || transaction.type === 'p2p_trade') ? 'pending' : 'confirmed',
      isMainTransaction: this.isMainTransactionType(transaction.type) ? 'true' : 'false',
      userId
    };

    let createdTransaction: Transaction | null = null;

    try {
      // ê±°ë˜ ê¸°ë¡ ë¨¼ì € ìƒì„±
      const [result] = await db
        .insert(transactions)
        .values(transactionData)
        .returning();
      createdTransaction = result;

      // confirmed ìƒíƒœì¸ ê²½ìš°ì—ë§Œ ìì‚° ì´ë™ ì²˜ë¦¬
      if (transactionData.status === 'confirmed') {
        await this.handleAssetMovement(userId, transaction, createdTransaction.id);
      }

      return createdTransaction;
    } catch (error) {
      // ìì‚° ì´ë™ ì¤‘ ì˜¤ë¥˜ ë°œìƒì‹œ ê±°ë˜ë¥¼ ì·¨ì†Œ ìƒíƒœë¡œ ë³€ê²½
      if (createdTransaction) {
        console.error('ìì‚° ì´ë™ ì¤‘ ì˜¤ë¥˜ ë°œìƒ. ê±°ë˜ ì·¨ì†Œ ì²˜ë¦¬:', error);
        await this.updateTransactionStatus(userId, createdTransaction.id, 'cancelled');
        throw error;
      }
      throw error;
    }
  }

  // ë©”ì¸ ê±°ë˜ íƒ€ì…ì¸ì§€ í™•ì¸í•˜ëŠ” í—¬í¼ í•¨ìˆ˜
  private isMainTransactionType(type: string): boolean {
    const mainTransactionTypes = [
      'cash_exchange',
      'cash_to_krw_account', 
      'cash_to_vnd_account',
      'vnd_account_to_krw_account',
      'krw_account_to_vnd_account',
      'bank_to_exchange',
      'exchange_purchase',
      'exchange_transfer',
      'p2p_trade'
    ];
    return mainTransactionTypes.includes(type);
  }

