  // í˜„ê¸ˆ ìì‚° ê¶Œì¢… ë°ì´í„° ì •ë¦¬ í•¨ìˆ˜
  public async cleanupCashDenominations(userId: string): Promise<void> {
    console.log('=== í˜„ê¸ˆ ìì‚° ê¶Œì¢… ë°ì´í„° ì •ë¦¬ ì‹œì‘ ===');

    const cashAssets = await db.select().from(assets).where(eq(assets.userId, userId));
    const cashAssetsOnly = cashAssets.filter(asset => asset.type === 'cash');

    for (const asset of cashAssetsOnly) {
      const metadata = asset.metadata as any || {};
      const denominations = metadata.denominations || {};

      let cleanedDenominations: Record<string, number> = {};
      let hasChanges = false;

      if (asset.currency === 'KRW') {
        // KRW ê¶Œì¢… ì •ë¦¬
        const validKRWDenoms = ['50000', '10000', '5000', '1000'];

        // ì¤‘ë³µ ê¶Œì¢… í†µí•© ë° ì˜ëª»ëœ ê¶Œì¢… ì œê±°
        for (const validDenom of validKRWDenoms) {
          let total = 0;

          // ë‹¤ì–‘í•œ í˜•íƒœì˜ í‚¤ í™•ì¸í•˜ê³  í†µí•©
          const keys = [`${validDenom}`, `${parseInt(validDenom).toLocaleString()}`];
          for (const key of keys) {
            if (denominations[key]) {
              total += parseInt(denominations[key] as string) || 0;
              if (key !== validDenom) hasChanges = true;
            }
          }

          if (total > 0) {
            cleanedDenominations[validDenom] = total;
          }
        }

        console.log(`KRW ê¶Œì¢… ì •ë¦¬: ${asset.name}`, {
          ê¸°ì¡´: denominations,
          ì •ë¦¬í›„: cleanedDenominations
        });

      } else if (asset.currency === 'USD') {
        // USD ê¶Œì¢… ì •ë¦¬
        const validUSDDenoms = ['100', '50', '20', '10', '5', '2', '1'];

        for (const validDenom of validUSDDenoms) {
          if (denominations[validDenom] && parseInt(denominations[validDenom] as string) > 0) {
            cleanedDenominations[validDenom] = parseInt(denominations[validDenom] as string);
          }
        }

        // ì˜ëª»ëœ ê¶Œì¢… ì œê±° í™•ì¸
        const originalKeys = Object.keys(denominations);
        if (originalKeys.some(key => !validUSDDenoms.includes(key))) {
          hasChanges = true;
        }

        console.log(`USD ê¶Œì¢… ì •ë¦¬: ${asset.name}`, {
          ê¸°ì¡´: denominations,
          ì •ë¦¬í›„: cleanedDenominations
        });

      } else if (asset.currency === 'VND') {
        // VNDëŠ” ì´ë¯¸ ì •ìƒì ìœ¼ë¡œ ë³´ì´ë¯€ë¡œ ê·¸ëŒ€ë¡œ ìœ ì§€
        cleanedDenominations = { ...denominations };

        console.log(`VND ê¶Œì¢… í™•ì¸: ${asset.name}`, {
          ê¶Œì¢…: denominations
        });
      }

      // ë³€ê²½ì‚¬í•­ì´ ìˆìœ¼ë©´ ì—…ë°ì´íŠ¸
      if (hasChanges || Object.keys(cleanedDenominations).length !== Object.keys(denominations).length) {
        await db.update(assets)
          .set({
            metadata: {
              ...metadata,
              denominations: cleanedDenominations
            },
            updatedAt: new Date()
          })
          .where(eq(assets.id, asset.id));

        console.log(`âœ… ${asset.name} ê¶Œì¢… ë°ì´í„° ì •ë¦¬ ì™„ë£Œ`);
      } else {
        console.log(`âœ“ ${asset.name} ê¶Œì¢… ë°ì´í„° ì •ìƒ`);
      }
    }

    console.log('=== í˜„ê¸ˆ ìì‚° ê¶Œì¢… ë°ì´í„° ì •ë¦¬ ì™„ë£Œ ===');
  }

  // ê¶Œì¢… í‚¤ ì •ê·œí™” í•¨ìˆ˜ (ì½¤ë§ˆ ì œê±°, í‘œì¤€í™”)
  private normalizeDenominationKey(key: string): string {
    // ì½¤ë§ˆ ì œê±°í•˜ê³  ìˆ«ìë§Œ ë‚¨ê¸°ê¸°
    const normalized = key.replace(/,/g, '').replace(/[^0-9]/g, '');
    return normalized;
  }

  // ê¶Œì¢… ë°ì´í„° ì •ê·œí™” í•¨ìˆ˜
  private normalizeDenominations(denominations: Record<string, any>): Record<string, number> {
    const normalized: Record<string, number> = {};

    for (const [key, value] of Object.entries(denominations)) {
      const normalizedKey = this.normalizeDenominationKey(key);
      if (normalizedKey && value) {
        const qty = parseInt(value as string) || 0;
        if (qty > 0) {
          // ì´ë¯¸ ì •ê·œí™”ëœ í‚¤ê°€ ìˆìœ¼ë©´ í•©ì‚°
          normalized[normalizedKey] = (normalized[normalizedKey] || 0) + qty;
        }
      }
    }

    return normalized;
  }
}

export const storage = new DatabaseStorage();

