import { sql } from "drizzle-orm";
import { pgTable, text, varchar, decimal, timestamp, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

// ê±°ë˜ ë‚´ì—­ í…Œì´ë¸”
export const transactions = pgTable("transactions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  type: text("type").notNull(), // 'bank_to_exchange', 'exchange_purchase', 'exchange_transfer', 'p2p_trade', 'cash_exchange', 'cash_change', 'exchange', 'transfer'
  fromAssetType: text("from_asset_type"), // 'bank', 'exchange', 'binance'
  fromAssetId: varchar("from_asset_id"),
  fromAssetName: text("from_asset_name").notNull(),
  toAssetType: text("to_asset_type"), // 'bank', 'exchange', 'binance'
  toAssetId: varchar("to_asset_id"),
  toAssetName: text("to_asset_name").notNull(),
  fromAmount: decimal("from_amount", { precision: 18, scale: 8 }).notNull(),
  toAmount: decimal("to_amount", { precision: 18, scale: 8 }).notNull(),
  rate: decimal("rate", { precision: 18, scale: 8 }).notNull(),
  fees: decimal("fees", { precision: 18, scale: 8 }).default("0"),
  profit: decimal("profit", { precision: 18, scale: 8 }).default("0"),
  marketPrice: decimal("market_price", { precision: 18, scale: 8 }), // ì‹œì¥ ê°€ê²©
  customPrice: decimal("custom_price", { precision: 18, scale: 8 }), // ì‚¬ìš©ì ì…ë ¥ ê°€ê²©
  status: text("status").default("pending"), // 'pending', 'confirmed', 'cancelled' - ê¸°ë³¸ê°’ pendingìœ¼ë¡œ ë³€ê²½
  memo: text("memo"),
  metadata: jsonb("metadata"), // ì¶”ê°€ ì •ë³´ (ê±°ë˜ì†Œëª…, P2P í”Œë«í¼ ë“±)
  parentTransactionId: varchar("parent_transaction_id"), // ê´€ë ¨ ë©”ì¸ ê±°ë˜ ID
  isMainTransaction: text("is_main_transaction").default("true"), // 'true'ëŠ” ë©”ì¸ ê±°ë˜, 'false'ëŠ” ë¶€ê°€ ê±°ë˜
  timestamp: timestamp("timestamp").defaultNow().notNull(),
});

// ìì‚° í…Œì´ë¸”
export const assets = pgTable("assets", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  type: text("type").notNull(), // 'cash', 'bank_account', 'exchange_asset', 'binance_asset'
  name: text("name").notNull(),
  currency: text("currency").notNull(),
  balance: decimal("balance", { precision: 18, scale: 8 }).default("0"),
  metadata: jsonb("metadata"), // ì§€í êµ¬ì„±, ê³„ì¢Œ ì •ë³´, ê±°ë˜ì†Œëª… ë“±
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// í™˜ìœ¨/ì‹œì„¸ ê¸°ë¡ í…Œì´ë¸”
export const rates = pgTable("rates", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  fromCurrency: text("from_currency").notNull(),
  toCurrency: text("to_currency").notNull(),
  rate: decimal("rate", { precision: 18, scale: 8 }).notNull(),
  source: text("source"), // 'manual', 'api', 'market'
  timestamp: timestamp("timestamp").defaultNow().notNull(),
});

// í˜„ì¬ ìš´ì˜ ì¤‘ì¸ í™˜ì „ìƒ ì‹œì„¸ í…Œì´ë¸” (1ê°œ ë ˆì½”ë“œ ìœ ì§€)
export const exchangeRates = pgTable("exchange_rates", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  fromCurrency: text("from_currency").notNull(), // 'USD', 'KRW'
  toCurrency: text("to_currency").notNull(), // 'VND'
  denomination: text("denomination"), // '100', '50', '20_10', '5_2_1', '50000', '10000', '5000_1000' ë“±
  goldShopRate: decimal("gold_shop_rate", { precision: 18, scale: 8 }), // ê¸ˆì€ë°© ì‹œì„¸ (ì°¸ê³ ìš©)
  myBuyRate: decimal("my_buy_rate", { precision: 18, scale: 8 }), // ë‚´ê°€ ì‚¬ëŠ” ê°€ê²© (ê³ ê°ì´ ë‚˜ì—ê²Œ íŒ” ë•Œ)
  mySellRate: decimal("my_sell_rate", { precision: 18, scale: 8 }), // ë‚´ê°€ íŒŒëŠ” ê°€ê²© (ê³ ê°ì´ ë‚˜ì—ê²Œì„œ ì‚´ ë•Œ)
  isActive: text("is_active").default("true"), // 'true', 'false'
  memo: text("memo"), // ê¸‰ë³€ìƒí™© ë©”ëª¨
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// í™˜ì „ìƒ ì‹œì„¸ ì´ë ¥ í…Œì´ë¸” (ë¶„ì„ìš©)
export const exchangeRateHistory = pgTable("exchange_rate_history", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  fromCurrency: text("from_currency").notNull(),
  toCurrency: text("to_currency").notNull(),
  denomination: text("denomination"),
  goldShopRate: decimal("gold_shop_rate", { precision: 18, scale: 8 }),
  myBuyRate: decimal("my_buy_rate", { precision: 18, scale: 8 }),
  mySellRate: decimal("my_sell_rate", { precision: 18, scale: 8 }),
  isActive: text("is_active").default("true"),
  memo: text("memo"),
  changePercentage: decimal("change_percentage", { precision: 5, scale: 2 }), // ì „ì¼ ëŒ€ë¹„ ë³€ë™ë¥ 
  recordDate: timestamp("record_date").notNull(), // í•´ë‹¹ ì‹œì„¸ê°€ ì ìš©ëœ ë‚ ì§œ
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// ì‚¬ìš©ì ì„¤ì • í…Œì´ë¸”
export const userSettings = pgTable("user_settings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().unique(),
  bithumbFeeRate: decimal("bithumb_fee_rate", { precision: 5, scale: 4 }).default("0.0004"), // 0.04%
  bithumbGrade: text("bithumb_grade").default("white"), // 'white', 'gold', 'platinum' ë“±
  defaultFeeRates: jsonb("default_fee_rates"), // ê° ê±°ë˜ì†Œë³„ ê¸°ë³¸ ìˆ˜ìˆ˜ë£Œ
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertTransactionSchema = createInsertSchema(transactions).omit({
  id: true,
  userId: true,
  timestamp: true,
});

export const insertAssetSchema = createInsertSchema(assets).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertRateSchema = createInsertSchema(rates).omit({
  id: true,
  timestamp: true,
});

export const insertExchangeRateSchema = createInsertSchema(exchangeRates).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertExchangeRateHistorySchema = createInsertSchema(exchangeRateHistory).omit({
  id: true,
  createdAt: true,
});

export const insertUserSettingsSchema = createInsertSchema(userSettings).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type Transaction = typeof transactions.$inferSelect;
export type InsertTransaction = z.infer<typeof insertTransactionSchema>;
export type Asset = typeof assets.$inferSelect;
export type InsertAsset = z.infer<typeof insertAssetSchema>;
export type Rate = typeof rates.$inferSelect;
export type InsertRate = z.infer<typeof insertRateSchema>;
export type ExchangeRate = typeof exchangeRates.$inferSelect;
export type InsertExchangeRate = z.infer<typeof insertExchangeRateSchema>;
export type ExchangeRateHistory = typeof exchangeRateHistory.$inferSelect;
export type InsertExchangeRateHistory = z.infer<typeof insertExchangeRateHistorySchema>;
export type UserSettings = typeof userSettings.$inferSelect;
export type InsertUserSettings = z.infer<typeof insertUserSettingsSchema>;

