  errors?: string[];
  // ë³´ìƒ ì‹œìŠ¤í…œ ê´€ë ¨ í•„ë“œ
  isCompensation?: boolean; // ë³´ìƒìš© ì¹´ë“œì¸ì§€ ì—¬ë¶€
  isCompensated?: boolean; // ì´ë¯¸ ë³´ìƒ ì²˜ë¦¬ëœ ì¹´ë“œì¸ì§€ ì—¬ë¶€
  originalCardId?: string; // ë³´ìƒ ì‹œ ì›ë³¸ ì¹´ë“œ ID
  originalCurrency?: string; // ì›ë³¸ ì¹´ë“œì˜ í†µí™”
