    // ì¬ê³  ë¶€ì¡± ê²½ê³ 
    const validation = validateInventory(card);
    if (!validation.isValid) {
      recommendations.push({
        type: 'warning',
        message: 'ë³´ìœ ëŸ‰ì´ ë¶€ì¡±í•©ë‹ˆë‹¤. ê¶Œì¢…ì„ ì¡°ì •í•´ì£¼ì„¸ìš”',
        action: () => {
          // ë³´ìœ ëŸ‰ì— ë§ì¶° ìë™ ì¡°ì •
          adjustToAvailableInventory(card);
        }
      });

      // ë³´ìƒ ë¶„ë°° ì¶”ì²œ ì¶”ê°€
      if (validation.errors.length > 0) {
        recommendations.push({
          type: 'info',
          message: 'ì¬ê³  ë¶€ì¡± ì‹œ ìë™ ë³´ìƒì„ ë°›ìœ¼ì‹œê² ìŠµë‹ˆê¹Œ?',
          action: () => {
            // 1. ë¶€ì¡±í•œ ê¶Œì¢…ì˜ ì…ë ¥ëœ ìˆ«ìë¥¼ 0ìœ¼ë¡œ ë§Œë“¤ê³  ì´ì•¡ ì¬ê³„ì‚°
            validation.errors.forEach(error => {
              if (error.includes('ë¶€ì¡±')) {
                // í•´ë‹¹ ì¹´ë“œì—ì„œ ë¶€ì¡±í•œ ê¶Œì¢…ì„ 0ìœ¼ë¡œ ì„¤ì •
                const cardId = card.id;
                const newDenominations = { ...card.denominations };

                // ë¶€ì¡±í•œ ê¶Œì¢…ë“¤ì„ ì°¾ì•„ì„œ 0ìœ¼ë¡œ ì„¤ì •
                Object.entries(card.denominations || {}).forEach(([denom, count]) => {
                  const denomValue = getDenominationValue(card.currency, denom);
                  const denomKey = card.currency === 'KRW' ? 
                    denomValue.toLocaleString() : denom;
                  const cashAsset = assets?.find((asset: any) => 
                    asset.name === `${card.currency} í˜„ê¸ˆ` && 
                    asset.currency === card.currency && 
                    asset.type === 'cash'
                  );
                  const availableCount = cashAsset?.metadata?.denominations?.[denomKey] || 0;

                  if (count > availableCount) {
                    newDenominations[denom] = 0;
                  }
                });

                // ê¶Œì¢…ë³„ ì´ì•¡ ë‹¤ì‹œ ê³„ì‚°
                let newTotalAmount = 0;
                Object.entries(newDenominations).forEach(([denom, count]) => {
                  const denomValue = getDenominationValue(card.currency, denom);
                  newTotalAmount += denomValue * count;
                });

                // ê¶Œì¢…ë³„ ë¶„ë°°ì™€ ì´ì•¡ì„ í•¨ê»˜ ì—…ë°ì´íŠ¸
                updateOutputCard(cardId, 'denominations', newDenominations);

                // VNDì˜ ê²½ìš° floor ì²˜ë¦¬ ì ìš©
                if (card.currency === 'VND') {
                  const flooredAmount = Math.floor(newTotalAmount / 1000) * 1000;
                  updateOutputCard(cardId, 'amount', flooredAmount.toLocaleString());
                } else {
                  updateOutputCard(cardId, 'amount', newTotalAmount.toLocaleString());
                }
              }
            });

            // 2. ì²« ë²ˆì§¸ ì¶œê¸ˆì¹´ë“œ ì ‘ê¸°
            if (outputCards.length > 0) {
              toggleCardCollapse(outputCards[0].id);
            }

            // 3. ìë™ ë³´ìƒ ì‹œìŠ¤í…œ ì‹¤í–‰ (ì´ë¯¸ ë³´ìƒ ì¹´ë“œê°€ ì—†ì„ ë•Œë§Œ)
            const hasCompensationCard = outputCards.some(card => card.isCompensation);
