    );
  };

  // ë³´ìœ  ìˆ˜ëŸ‰ ê²€ì¦ í•¨ìˆ˜ (TransactionFormì—ì„œ ì´ì‹)
  const validateInventory = (card: TransactionCard): { isValid: boolean; errors: string[] } => {
    const errors: string[] = [];

    if (card.type !== 'cash' || !card.amount) {
      return { isValid: true, errors: [] };
    }

    const amount = parseCommaFormattedNumber(card.amount);
    if (amount <= 0) {
      return { isValid: true, errors: [] };
    }

    // í•´ë‹¹ í†µí™”ì˜ í˜„ê¸ˆ ìì‚° ì°¾ê¸°
    const cashAsset = assets.find(asset => 
      asset.name === `${card.currency} í˜„ê¸ˆ` && 
      asset.currency === card.currency && 
      asset.type === 'cash'
    );

    if (!cashAsset || !cashAsset.metadata?.denominations) {
      errors.push(`${card.currency} í˜„ê¸ˆ ìì‚°ì´ ì—†ìŠµë‹ˆë‹¤`);
      return { isValid: false, errors };
    }

    const denomComposition = cashAsset.metadata.denominations;

    // ê¶Œì¢…ë³„ ë¶„ë°°ê°€ ìˆëŠ” ê²½ìš° ê²€ì¦
    if (Object.keys(card.denominations).length > 0) {
      for (const [denom, requiredCount] of Object.entries(card.denominations)) {
        const denomKey = card.currency === 'KRW' ? 
          parseInt(denom).toLocaleString() : denom.toString();
        const availableCount = denomComposition[denomKey] || 0;

        if (requiredCount > availableCount) {
          errors.push(
            `${card.currency === 'KRW' ? parseInt(denom).toLocaleString() : parseInt(denom).toLocaleString()} ${
              card.currency === 'KRW' ? 'ì›' : 
              card.currency === 'USD' ? 'ë‹¬ëŸ¬' : 'ë™'
            }ê¶Œì´ ${requiredCount - availableCount}ì¥ ë¶€ì¡±í•©ë‹ˆë‹¤`
          );
        }
      }
    }

    return { isValid: errors.length === 0, errors };
  };

  // ë‹¤ì¤‘ ì¶œê¸ˆì¹´ë“œ ë³´ìƒ ì‹œìŠ¤í…œ - ì¬ê³  ë¶€ì¡± ì‹œ ë‹¤ë¥¸ í†µí™”ë¡œ ë³´ìƒ
  const handleInventoryShortage = (shortageCard: TransactionCard, shortageInfo: { denom: string; shortfall: number }) => {
    // ë¬´í•œ ë£¨í”„ ë°©ì§€: í•œ ë²ˆë§Œ ë³´ìƒ í—ˆìš©
    if (shortageCard.isCompensated) {
      console.log('ì´ë¯¸ ë³´ìƒ ì²˜ë¦¬ëœ ì¹´ë“œì…ë‹ˆë‹¤');
      return false;
    }

    // ë³´ìƒ ê°€ëŠ¥í•œ í†µí™” ìš°ì„ ìˆœìœ„ (VND ìš°ì„ )
    const compensationCurrencies = ['VND', 'KRW', 'USD'].filter(curr => curr !== shortageCard.currency);

    for (const compensationCurrency of compensationCurrencies) {
      // í•´ë‹¹ í†µí™” ì¬ê³  í™•ì¸
      const cashAsset = assets.find(asset => 
        asset.name === `${compensationCurrency} í˜„ê¸ˆ` && 
        asset.currency === compensationCurrency && 
        asset.type === 'cash'
      );

      if (cashAsset && cashAsset.balance > 0) {
        const compensationAmount = calculateCompensationAmount(shortageCard, shortageInfo, compensationCurrency);

        if (compensationAmount > 0) {
          // ë³´ìƒìš© ìƒˆ ì¶œê¸ˆì¹´ë“œ ìƒì„±
          createCompensationCard(compensationCurrency, compensationAmount, shortageCard, shortageInfo);

          // ì›ë˜ ì¹´ë“œì— ë³´ìƒ í‘œì‹œ
          markCardAsCompensated(shortageCard.id, compensationCurrency, compensationAmount);

          return true;
        }
      }
    }

    return false;
  };

  // ë³´ìƒ ê¸ˆì•¡ ê³„ì‚° (ì˜¬ë¦¼ ì²˜ë¦¬ í¬í•¨)
  const calculateCompensationAmount = (shortageCard: TransactionCard, shortageInfo: { denom: string; shortfall: number }, compensationCurrency: string): number => {
    // ë¶€ì¡±í•œ ê¸ˆì•¡ ê³„ì‚°
    const shortfallValue = getDenominationValue(shortageCard.currency, shortageInfo.denom) * shortageInfo.shortfall;

    // í™˜ìœ¨ ì ìš©
    const exchangeRate = getExchangeRate(shortageCard.currency, compensationCurrency);
    const baseCompensationAmount = shortfallValue * exchangeRate;

    // ì˜¬ë¦¼ ì²˜ë¦¬ë¡œ ì†ì‹¤ ìµœì†Œí™” (ë² íŠ¸ë‚¨ë™ì˜ ê²½ìš° 1000ë™ ë‹¨ìœ„ë¡œ ì˜¬ë¦¼)
    if (compensationCurrency === 'VND') {
      return Math.ceil(baseCompensationAmount / 1000) * 1000;
    } else if (compensationCurrency === 'KRW') {
      return Math.ceil(baseCompensationAmount / 1000) * 1000;
    } else {
      return Math.ceil(baseCompensationAmount);
    }
  };

  // ë³´ìƒ í†µí™” ê²°ì •
  const getCompensationCurrency = (originalCurrency: string): string => {
    if (originalCurrency === 'USD') return 'VND';
    if (originalCurrency === 'VND') return 'KRW';
    if (originalCurrency === 'KRW') return 'VND';
    return 'VND'; // ê¸°ë³¸ê°’
  };


  // ë³´ìƒìš© ìƒˆ ì¶œê¸ˆì¹´ë“œ ìƒì„± (ê¶Œì¢…ë³„ ë¶„ë°° í¬í•¨)
  const createCompensationCard = (currency: string, amount: number, originalCard: TransactionCard, shortageInfo: { denom: string; shortfall: number }) => {
    // ì „ì—­ ì¤‘ë³µ ë°©ì§€ ì²´í¬
    if (isCreatingCompensation.current) {
      console.log('ì´ë¯¸ ë³´ìƒì¹´ë“œ ìƒì„± ì¤‘ì…ë‹ˆë‹¤. ì¤‘ë³µ ìƒì„±ì„ ë°©ì§€í•©ë‹ˆë‹¤.');
      return;
    }

    // ì¤‘ë³µ ë³´ìƒ ì¹´ë“œ ìƒì„± ë°©ì§€ - ì´ë¯¸ ê°™ì€ ì›ë³¸ ì¹´ë“œì— ëŒ€í•œ ë³´ìƒ ì¹´ë“œê°€ ìˆëŠ”ì§€ í™•ì¸
    const existingCompensation = outputCards.find(card => 
      card.isCompensation && card.originalCardId === originalCard.id
    );

    if (existingCompensation) {
      console.log(`ì›ë³¸ ì¹´ë“œ ${originalCard.id}ì— ëŒ€í•œ ë³´ìƒ ì¹´ë“œê°€ ì´ë¯¸ ì¡´ì¬í•©ë‹ˆë‹¤.`);
      return;
    }

    // ì „ì²´ ë³´ìƒì¹´ë“œ ì¡´ì¬ ì—¬ë¶€ ì²´í¬
    const totalCompensationCards = outputCards.filter(card => card.isCompensation).length;
    if (totalCompensationCards > 0) {
      console.log('ì´ë¯¸ ë‹¤ë¥¸ ë³´ìƒì¹´ë“œê°€ ì¡´ì¬í•©ë‹ˆë‹¤. ì¶”ê°€ ìƒì„±ì„ ì¤‘ì§€í•©ë‹ˆë‹¤.');
      return;
    }

    // ë³´ìƒì¹´ë“œ ìƒì„± ì‹œì‘ í”Œë˜ê·¸ ì„¤ì •
    isCreatingCompensation.current = true;

    // ë³´ìƒ ê¸ˆì•¡ì— ëŒ€í•œ ê¶Œì¢…ë³„ ë¶„ë°° ìë™ ê³„ì‚°
    const compensationDenominations = calculateOptimalDenominations(currency, amount);

    const newCard: TransactionCard = {
      id: `compensation-${Date.now()}`,
      type: 'cash',
      currency: currency,
      amount: addCommas(amount.toString()), // ì½¤ë§ˆ í¬í•¨í•œ í˜•íƒœë¡œ ì €ì¥
      denominations: compensationDenominations,
      isCompensation: true,
      originalCardId: originalCard.id,
      originalCurrency: originalCard.currency,
      originalAmount: `${getDenominationValue(originalCard.currency, shortageInfo.denom) * shortageInfo.shortfall}`,
      compensationReason: `${originalCard.currency} ${shortageInfo.denom}ê¶Œ ${shortageInfo.shortfall}ì¥ ë¶€ì¡±ìœ¼ë¡œ ì¸í•œ ë³´ìƒ`
    };

    console.log(`ë³´ìƒ ì¹´ë“œ ìƒì„±: ${currency} ${amount} (ì›ë³¸: ${originalCard.id})`);

    // ë³´ìƒ ì¹´ë“œë¥¼ ì¶œê¸ˆ ì¹´ë“œ ëª©ë¡ì— ì¶”ê°€
    setOutputCards(prev => [...prev, newCard]);

    // ë³´ìƒì¹´ë“œ ìƒì„± ì™„ë£Œ í›„ í”Œë˜ê·¸ í•´ì œ (ì•½ê°„ì˜ ì§€ì—° í›„)
    setTimeout(() => {
      isCreatingCompensation.current = false;
    }, 500);

    // ì‚¬ìš©ìì—ê²Œ ëª…í™•í•œ ë³´ìƒ íë¦„ ì•Œë¦¼
    const originalAmount = parseCommaFormattedNumber(originalCard.amount) || 0;
    const shortfallValue = getDenominationValue(originalCard.currency, shortageInfo.denom) * shortageInfo.shortfall;

    toast({
      title: "ì¬ê³  ë¶€ì¡± ìë™ ë³´ìƒ",
      description: `${originalAmount.toLocaleString()} ${originalCard.currency} â†’ ${(originalAmount - shortfallValue).toLocaleString()} ${originalCard.currency} (ì¬ê³ ë¶€ì¡± ${shortfallValue.toLocaleString()}) â†’ ë³´ìƒì¹´ë“œ: ${amount.toLocaleString()} ${currency}`,
      duration: 6000,
    });
  };

  // ë³´ìƒ ê¸ˆì•¡ì— ëŒ€í•œ ìµœì  ê¶Œì¢…ë³„ ë¶„ë°° ê³„ì‚°
  const calculateOptimalDenominations = (currency: string, amount: number): Record<string, number> => {
    const denominations: Record<string, number> = {};
    let remainingAmount = amount;

    // í†µí™”ë³„ ê¶Œì¢… ì •ì˜ (í° ì•¡ë©´ë¶€í„°)
    const currencyDenominations: Record<string, number[]> = {
      'VND': [500000, 200000, 100000, 50000, 20000, 10000, 5000, 2000, 1000],
      'KRW': [50000, 10000, 5000, 1000],
      'USD': [100, 50, 20, 10, 5, 1]
    };

    const availableDenoms = currencyDenominations[currency] || [];

    // í° ê¶Œì¢…ë¶€í„° ì°¨ë¡€ë¡œ ê³„ì‚°
    for (const denom of availableDenoms) {
      if (remainingAmount >= denom) {
        const count = Math.floor(remainingAmount / denom);
        if (count > 0) {
          denominations[denom.toString()] = count;
          remainingAmount -= count * denom;
        }
      }
    }

    return denominations;
  };

  // ì¹´ë“œë¥¼ ë³´ìƒ ì²˜ë¦¬ëœ ê²ƒìœ¼ë¡œ í‘œì‹œ
  const markCardAsCompensated = (cardId: string, compensationCurrency: string, compensationAmount: number) => {
    setOutputCards(prev => prev.map(card => {
      if (card.id === cardId) {
        return {
          ...card,
          isCompensated: true,
          compensationInfo: {
            currency: compensationCurrency,
            amount: compensationAmount
          }
        };
      }
      return card;
    }));
  };

  // ê°œì„ ëœ ì¬ê³  ê²€ì¦ ë° ìë™ ë³´ìƒ ì‹œìŠ¤í…œ (ì¤‘ë³µ ë°©ì§€ í¬í•¨)
  const checkInventoryWithCompensation = () => {
    let hasShortage = false;
    const processedShortages = new Set<string>(); // ì¤‘ë³µ ë°©ì§€ìš©

    // ì´ë¯¸ ë³´ìƒ ì¹´ë“œê°€ ì¡´ì¬í•˜ë©´ ì¶”ê°€ ë³´ìƒ ì¤‘ë‹¨
    const existingCompensationCards = outputCards.filter(card => card.isCompensation);
    if (existingCompensationCards.length > 0) {
      console.log('ì´ë¯¸ ë³´ìƒ ì¹´ë“œê°€ ì¡´ì¬í•©ë‹ˆë‹¤. ì¶”ê°€ ë³´ìƒì„ ì¤‘ë‹¨í•©ë‹ˆë‹¤.');
      return false;
    }

    outputCards.forEach(card => {
      if (card.type === 'cash' && !card.isCompensation) {
        const validation = validateInventory(card);

        if (!validation.isValid && validation.errors.length > 0) {
          // ì¬ê³  ë¶€ì¡± ì—ëŸ¬ì—ì„œ ì„¸ë¶€ ì •ë³´ ì¶”ì¶œ
          validation.errors.forEach(error => {
            // ê¶Œì¢…ë³„ ë¶€ì¡± íŒ¨í„´ ë§¤ì¹˜: "1 ë‹¬ëŸ¬ê¶Œì´ 1ì¥ ë¶€ì¡±í•©ë‹ˆë‹¤", "50,000 ì›ê¶Œì´ 2ì¥ ë¶€ì¡±í•©ë‹ˆë‹¤" ë“±
            const shortageMatch = error.match(/(\d+(?:,\d+)*)\s*(?:ë‹¬ëŸ¬|ì›|ë™)?ê¶Œì´ (\d+)ì¥ ë¶€ì¡±í•©ë‹ˆë‹¤/);
            if (shortageMatch) {
              const denom = shortageMatch[1].replace(/,/g, ''); // ìˆ«ì ë¶€ë¶„ë§Œ ì¶”ì¶œ
              const shortfall = parseInt(shortageMatch[2]);

              // ì¤‘ë³µ ë³´ìƒ ë°©ì§€ ì²´í¬
              const shortageKey = `${card.id}-${denom}-${shortfall}`;
              if (processedShortages.has(shortageKey)) {
                return;
              }

              // ì´ë¯¸ ì´ ì¹´ë“œì— ëŒ€í•œ ë³´ìƒì¹´ë“œê°€ ì¡´ì¬í•˜ëŠ”ì§€ í™•ì¸
              const existingCompensation = outputCards.find(outCard => 
                outCard.isCompensation && outCard.originalCardId === card.id
              );

              if (existingCompensation) {
                return;
              }

              processedShortages.add(shortageKey);

              // ìë™ ë³´ìƒ ì‹œë„ - ì§ì ‘ ë³´ìƒ ì¹´ë“œ ìƒì„±
              const compensationCurrency = getCompensationCurrency(card.currency);
              const compensationAmount = calculateCompensationAmount(card, { denom, shortfall }, compensationCurrency);

              // ë³´ìƒ ì¹´ë“œ ìƒì„± (ì¤‘ë³µ ì²´í¬ í¬í•¨)
              console.log(`ì¬ê³  ë¶€ì¡± ê°ì§€: ${card.currency} ${denom}ê¶Œ ${shortfall}ì¥ ë¶€ì¡±`);
              createCompensationCard(compensationCurrency, compensationAmount, card, { denom, shortfall });
              hasShortage = true;

              // ì²« ë²ˆì§¸ ë¶€ì¡±í•œ ê¶Œì¢…ì— ëŒ€í•´ì„œë§Œ ë³´ìƒ ìƒì„±í•˜ê³  í•¨ìˆ˜ ì¢…ë£Œ
              return hasShortage;
            }
          });
        }
      }
    });

    return hasShortage;
  };

  // ì¶œê¸ˆ ì¹´ë“œ ê¶Œì¢…ë³„ ì´ì•¡ê³¼ ëª©í‘œ ê¸ˆì•¡ ì¼ì¹˜ ê²€ì¦
  const validateOutputCardAmounts = () => {
    const errors: string[] = [];

    outputCards.forEach((card, index) => {
      if (card.type === 'cash' && card.denominations && Object.keys(card.denominations).length > 0) {
        // ê¶Œì¢…ë³„ ì´ì•¡ ê³„ì‚°
        let denominationTotal = 0;
        Object.entries(card.denominations).forEach(([denom, count]) => {
          const denomValue = getDenominationValue(card.currency, denom);
          denominationTotal += denomValue * count;
        });

        // ëª©í‘œ ê¸ˆì•¡
        const targetAmount = parseCommaFormattedNumber(card.amount) || 0;

        // ê¸ˆì•¡ ë¶ˆì¼ì¹˜ ê²€ì‚¬
        if (denominationTotal !== targetAmount) {
          const difference = Math.abs(denominationTotal - targetAmount);
          const currencyUnit = card.currency === 'KRW' ? 'ì›' : card.currency === 'VND' ? 'ë™' : 'ë‹¬ëŸ¬';
          errors.push(
            `ì¶œê¸ˆ ì¹´ë“œ ${index + 1}: ê¶Œì¢…ë³„ ì´ì•¡(${denominationTotal.toLocaleString()}${currencyUnit})ê³¼ ` + 
            `ëª©í‘œ ê¸ˆì•¡(${targetAmount.toLocaleString()}${currencyUnit})ì´ ${difference.toLocaleString()}${currencyUnit} ì°¨ì´ë‚©ë‹ˆë‹¤`
          );
        }
      }
    });

    return errors;
  };

  // ì „ì²´ ê±°ë˜ ìœ íš¨ì„± ê²€ì¦
  const validateTransaction = () => {
    const errors: string[] = [];

    // ì¬ê³  ë¶€ì¡± ì‹œ ìë™ ë³´ìƒ ì‹œìŠ¤í…œ ì‹¤í–‰ (ê²€ì¦ ì „ì—, ì´ë¯¸ ë³´ìƒì¹´ë“œê°€ ì—†ì„ ë•Œë§Œ)
    const hasCompensationCard = outputCards.some(card => card.isCompensation);
    if (!hasCompensationCard && !isCreatingCompensation.current) {
      checkInventoryWithCompensation();
    }

    // ê¶Œì¢…ë³„ ì´ì•¡ê³¼ ëª©í‘œ ê¸ˆì•¡ ì¼ì¹˜ ê²€ì¦ ì¶”ê°€
    const amountValidationErrors = validateOutputCardAmounts();
    errors.push(...amountValidationErrors);

    // ê³„ì¢Œì´ì²´ ê±°ë˜ì¸ì§€ í™•ì¸
    const isAccountTransfer = selectedTransactionType.includes('_to_') && 
      (selectedTransactionType.includes('_account') || outputCards.some(card => card.type === 'account'));

    // ìµëª… ê±°ë˜ê°€ ì•„ë‹ˆê³  ê³„ì¢Œì´ì²´ì¸ ê²½ìš°ì—ë§Œ ê³ ê° ì •ë³´ í•„ìˆ˜
    if (!isAnonymousTransaction && isAccountTransfer && !customerName.trim()) {
      errors.push('ê³„ì¢Œì´ì²´ ê±°ë˜ ì‹œ ê³ ê°ëª…ì´ í•„ìš”í•©ë‹ˆë‹¤');
    }

    // ê³„ì¢Œì´ì²´ ê±°ë˜ì¸ ê²½ìš° ê³ ê° ê³„ì¢Œ ì •ë³´ í•„ìˆ˜
    if (isAccountTransfer) {
      if (!customerAccountInfo.bankName.trim()) {
        errors.push('ìˆ˜ì‹  ì€í–‰ëª…ì„ ì…ë ¥í•´ì£¼ì„¸ìš”');
      }
      if (!customerAccountInfo.accountNumber.trim()) {
        errors.push('ìˆ˜ì‹  ê³„ì¢Œë²ˆí˜¸ë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš”');
      }
      if (!customerAccountInfo.accountHolder.trim()) {
