    });

    // í˜„ì¬ ë³´ê³  ìˆëŠ” í˜„ê¸ˆ ìì‚°ì˜ í†µí™”ì— ë”°ë¼ í•´ë‹¹ ê¶Œì¢…ë§Œ í‘œì‹œ
    if (cashAsset.currency === 'KRW') {
      // KRW í˜„ê¸ˆ ìƒì„¸ í˜ì´ì§€ì—ì„œ í™˜ì „ ê±°ë˜ ì²˜ë¦¬
      const isKrwIncrease = transaction.fromAssetName === cashAsset.name; // KRWê°€ fromAssetì´ë©´ ì¦ê°€

      if (isKrwIncrease) {
        // KRWâ†’ë‹¤ë¥¸í†µí™”: denominationAmountsëŠ” KRW ê¶Œì¢… (KRW ì¦ê°€)
        Object.entries(denominationAmounts).forEach(([denom, amount]) => {
          if (amount && parseFloat(amount as string) > 0) {
            denominationChanges[denom] = parseInt(amount as string); // KRW ì¦ê°€
          }
        });
      } else {
        // ë‹¤ë¥¸í†µí™”â†’KRW: krwBreakdown ì‚¬ìš© (KRW ê°ì†Œ)
        if (Object.keys(krwBreakdown).length > 0) {
          // krwBreakdownì´ ìˆìœ¼ë©´ ì‚¬ìš©
          Object.entries(krwBreakdown).forEach(([denom, amount]: [string, any]) => {
            if (amount && parseInt(amount.toString()) > 0) {
              denominationChanges[denom] = -parseInt(amount.toString()); // KRW ê°ì†Œ
            }
          });
        }
      }
    } else if (cashAsset.currency === 'VND') {
      // VND í˜„ê¸ˆ ìƒì„¸ í˜ì´ì§€ì—ì„œ í™˜ì „ ê±°ë˜ ì²˜ë¦¬
      const isVndIncrease = transaction.fromAssetName === cashAsset.name; // VNDê°€ fromAssetì´ë©´ ì¦ê°€

      if (isVndIncrease) {
        // VNDâ†’ë‹¤ë¥¸í†µí™”: denominationAmountsëŠ” VND ê¶Œì¢… (VND ì¦ê°€)
        Object.entries(denominationAmounts).forEach(([denom, amount]) => {
          if (amount && parseFloat(amount as string) > 0) {
            denominationChanges[denom] = parseInt(amount as string); // VND ì¦ê°€
          }
        });
      } else {
        // ë‹¤ë¥¸í†µí™”â†’VND: vndBreakdown ì‚¬ìš© (VND ê°ì†Œ)
        if (Object.keys(vndBreakdown).length > 0) {
          Object.entries(vndBreakdown).forEach(([denom, amount]: [string, any]) => {
            if (amount && parseInt(amount.toString()) > 0) {
              denominationChanges[denom] = -parseInt(amount.toString()); // VND ê°ì†Œ
            }
          });
        }
      }
    } else if (cashAsset.currency === 'USD') {
      // USD í˜„ê¸ˆ ìƒì„¸ í˜ì´ì§€ì—ì„œ í™˜ì „ ê±°ë˜ ì²˜ë¦¬
      const isUsdIncrease = transaction.fromAssetName === cashAsset.name; // USDê°€ fromAssetì´ë©´ ì¦ê°€

      if (isUsdIncrease) {
        // USDâ†’ë‹¤ë¥¸í†µí™”: denominationAmountsëŠ” USD ê¶Œì¢… (USD ì¦ê°€)
        Object.entries(denominationAmounts).forEach(([denom, amount]) => {
          if (amount && parseFloat(amount as string) > 0) {
            denominationChanges[denom] = parseInt(amount as string); // USD ì¦ê°€
          }
        });
      } else {
        // ë‹¤ë¥¸í†µí™”â†’USD: usdBreakdown ì‚¬ìš© (USD ê°ì†Œ)
        if (Object.keys(usdBreakdown).length > 0) {
          Object.entries(usdBreakdown).forEach(([denom, amount]: [string, any]) => {
            if (amount && parseInt(amount.toString()) > 0) {
              denominationChanges[denom] = -parseInt(amount.toString()); // USD ê°ì†Œ
            }
          });
        }
      }
    }

    console.log('ìµœì¢… denominationChanges:', denominationChanges);
  }



  // í†µí™”ë³„ ì§€í ë‹¨ìœ„ ì •ì˜
  const getCurrencyDenominations = (currency: string) => {
    switch (currency) {
      case 'KRW':
        return ['50000', '10000', '5000', '1000'];
      case 'USD':
        return ['100', '50', '20', '10', '5', '2', '1'];
      case 'VND':
        return ['500000', '200000', '100000', '50000', '20000', '10000', '5000', '2000', '1000'];
      case 'MIXED': // í˜¼í•©ëœ ê²½ìš° ëª¨ë“  ê¶Œì¢…
        return ['500000', '200000', '100000', '50000', '20000', '10000', '5000', '2000', '1000'];
      default:
        return [];
    }
  };

  // í†µí™” ê²°ì • - ì „ë‹¬ë°›ì€ cashAssetì˜ í†µí™” ì‚¬ìš©
  const getCurrency = () => {
    return cashAsset.currency; // í˜„ì¬ ë³´ê³  ìˆëŠ” í˜„ê¸ˆ ìì‚°ì˜ í†µí™”
  };

  const currency = getCurrency();
  const denominations = getCurrencyDenominations(currency);

  // í†µí™” ê¸°í˜¸
  const getCurrencySymbol = (currency: string) => {
    switch (currency) {
      case 'KRW': return 'ì›';
      case 'USD': return 'ë‹¬ëŸ¬';
      case 'VND': return 'ë™';
      default: return '';
    }
  };

  // ì§€í ì´ë¦„ - í˜¼í•©ëœ ê²½ìš° ê¶Œì¢… ê°’ìœ¼ë¡œ í†µí™” íŒë‹¨
  const getDenominationName = (denomination: string, currency: string) => {
    // USD íŠ¹ìˆ˜ ì¼€ì´ìŠ¤ ì²˜ë¦¬
    if (denomination === '20/10' || denomination === '20_10') {
      return '20/10ë‹¬ëŸ¬ê¶Œ';
    }

    // ì›ë˜ denominationì— ì½¤ë§ˆê°€ ìˆìœ¼ë©´ ê·¸ëŒ€ë¡œ ì‚¬ìš©, ì—†ìœ¼ë©´ ìˆ«ìë¡œ ë³€í™˜ í›„ ì½¤ë§ˆ ì¶”ê°€
    let displayValue = denomination;
    if (!denomination.includes(',') && !isNaN(parseInt(denomination))) {
      const num = parseInt(denomination);
      displayValue = num.toLocaleString();
    }

    if (currency === 'MIXED') {
      // ê¶Œì¢… ê°’ìœ¼ë¡œ í†µí™” íŒë‹¨
      const numericValue = parseInt(denomination.replace(/,/g, ''));
      if ([1000, 5000, 10000, 50000].includes(numericValue)) {
        return `${displayValue}ì›ê¶Œ`;
      } else {
        return `${displayValue}ë™ê¶Œ`;
      }
    }

    switch (currency) {
      case 'KRW':
        return `${displayValue}ì›ê¶Œ`;
      case 'USD':
        // USDëŠ” ì½¤ë§ˆ ì—†ì´ í‘œì‹œ
        const usdNum = parseInt(denomination.replace(/,/g, ''));
        return `${usdNum}ë‹¬ëŸ¬ê¶Œ`;
      case 'VND':
        return `${displayValue}ë™ê¶Œ`;
      default:
        return `${displayValue}`;
    }
  };

  // ì¦ê°€/ê°ì†Œ ê³„ì‚°
  const getChangeInfo = () => {
    const increases: Array<{ denomination: string; change: number; value: number }> = [];
    const decreases: Array<{ denomination: string; change: number; value: number }> = [];

    // denominationChangesì—ì„œ ì§ì ‘ ê³„ì‚°
    Object.entries(denominationChanges).forEach(([denom, change]) => {
      const changeNum = typeof change === 'number' ? change : parseInt(change as string) || 0;
      if (changeNum === 0) return;

      let denomValue = parseInt(denom.replace(/[,/]/g, ''));
      if (denom === '20/10' || denom === '20_10') denomValue = 30; // 20+10 = 30

      if (changeNum > 0) {
        increases.push({
          denomination: denom,
          change: changeNum,
          value: changeNum * denomValue
        });
      } else {
        decreases.push({
          denomination: denom,
          change: Math.abs(changeNum),
          value: Math.abs(changeNum * denomValue)
        });
      }
    });

    // ëª¨ë“  í†µí™”ì—ì„œ ê³ ì•¡ê¶Œë¶€í„° ì •ë ¬
    const sortByDenomination = (arr: Array<{ denomination: string; change: number; value: number }>) => {
      return arr.sort((a, b) => {
        // 20/10 ë‹¬ëŸ¬ íŠ¹ìˆ˜ ì¼€ì´ìŠ¤ ì²˜ë¦¬
        let aValue = 0;
        let bValue = 0;

        if (a.denomination === '20/10') {
          aValue = 20;
        } else if (a.denomination === '20_10') {
          aValue = 30; // 20+10=30 ê°€ì¹˜ë¡œ ê³„ì‚°
        } else {
          aValue = parseInt(a.denomination.replace(/,/g, ''));
        }

        if (b.denomination === '20/10') {
          bValue = 20;
        } else if (b.denomination === '20_10') {
          bValue = 30; // 20+10=30 ê°€ì¹˜ë¡œ ê³„ì‚°
        } else {
          bValue = parseInt(b.denomination.replace(/,/g, ''));
        }

        return bValue - aValue; // ë‚´ë¦¼ì°¨ìˆœ ì •ë ¬ (ê³ ì•¡ê¶Œ ë¨¼ì €)
      });
    };

    sortByDenomination(increases);
    sortByDenomination(decreases);

    return { increases, decreases };
  };

  const { increases, decreases } = getChangeInfo();
  const totalIncrease = increases.reduce((sum, item) => sum + item.value, 0);
  const totalDecrease = decreases.reduce((sum, item) => sum + item.value, 0);
  const netChange = totalIncrease - totalDecrease; // ì¦ê°€ - ê°ì†Œ = ìˆœë³€ë™

  const formatDateTime = (timestamp: string | Date) => {
    const date = new Date(timestamp);
    return {
      date: date.toLocaleDateString('ko-KR', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      }),
      time: date.toLocaleTimeString('ko-KR', {
        hour: '2-digit',
        minute: '2-digit'
      })
    };
  };

  const { date, time } = formatDateTime(transaction.timestamp);

  // í™˜ì „ íƒ€ì… í…ìŠ¤íŠ¸ ê²°ì • - í˜„ì¬ ë³´ê³  ìˆëŠ” í˜„ê¸ˆ ìì‚°ì— ë§ì¶˜ í‘œê¸°
  const getExchangeTypeText = (transaction: Transaction, currency: string) => {
    if ((transaction.type as string) !== 'cash_exchange') return '';

    const metadata = transaction.metadata as any;

    // ê±°ë˜ ìœ í˜•ì— ë”°ë¥¸ ì •í™•í•œ í‘œê¸°
    if (currency === 'KRW') {
      // KRW í˜„ê¸ˆ ìƒì„¸ í˜ì´ì§€ - ì‚¬ì—…ìê°€ KRWë¥¼ ë°›ìŒ (ìˆ˜ë ¹)
      return 'KRW í˜„ê¸ˆ í™˜ì „ ìˆ˜ë ¹';
    } else if (currency === 'VND') {
      // VND í˜„ê¸ˆ ìƒì„¸ í˜ì´ì§€ - ê±°ë˜ ìœ í˜•ì— ë”°ë¼ ìˆ˜ë ¹/ì§€ê¸‰ íŒë‹¨
      const isVndIncrease = transaction.fromAssetName === cashAsset.name;

      if (isVndIncrease) {
        // VNDâ†’USD í™˜ì „: ì‚¬ì—…ìê°€ VNDë¥¼ ë°›ìŒ (ìˆ˜ë ¹)
        const toCurrency = metadata?.toCurrency || 'USD';
        return `${toCurrency} í˜„ê¸ˆ ì§€ê¸‰ í™˜ì „â†’ VND ìˆ˜ëŸ‰ ì¦ê°€`;
      } else {
        // KRWâ†’VND í™˜ì „: ì‚¬ì—…ìê°€ VNDë¥¼ ì¤Œ (ì§€ê¸‰)
        return 'VND í˜„ê¸ˆ í™˜ì „ ì§€ê¸‰';
      }
    } else if (currency === 'USD') {
      // USD í˜„ê¸ˆ ìƒì„¸ í˜ì´ì§€ - ì‚¬ì—…ìê°€ USDë¥¼ ì¤Œ (ì§€ê¸‰)
      return 'USD í˜„ê¸ˆ í™˜ì „ ì§€ê¸‰';
    }

    return 'í˜„ê¸ˆí™˜ì „';
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[95vh] w-[98vw] sm:w-[95vw] overflow-hidden flex flex-col p-3 sm:p-4 md:p-6">
        {/* ê³ ì • í—¤ë” - ëª¨ë°”ì¼ ìµœì í™” */}
        <DialogHeader className="flex-shrink-0 pb-3 sm:pb-4 border-b">
          <DialogTitle className="flex items-center gap-2 text-base sm:text-lg">
            <Banknote size={18} className="sm:w-5 sm:h-5" />
            <span className="leading-tight">
              {(transaction.type as string) === 'cash_exchange' ? 'í˜„ê¸ˆ í™˜ì „ ìƒì„¸ ë‚´ì—­' : 'í˜„ê¸ˆ ì¦ê° ìƒì„¸ ë‚´ì—­'}
            </span>
          </DialogTitle>
          <DialogDescription className="text-xs sm:text-sm leading-tight">
            {(transaction.type as string) === 'cash_exchange' 
              ? `${getExchangeTypeText(transaction, currency)} - ${date} ${time}` 
              : `${transaction.toAssetName} - ${date} ${time}`}
          </DialogDescription>
        </DialogHeader>

        {/* ìŠ¤í¬ë¡¤ ê°€ëŠ¥í•œ ë‚´ìš© ì˜ì—­ - ëª¨ë°”ì¼ ìµœì í™” */}
        <div className="flex-1 overflow-y-auto mt-3 sm:mt-4">
          <div className="space-y-3 sm:space-y-6">
          {/* ì „ì²´ ìš”ì•½ - ëª¨ë°”ì¼ ìµœì í™” */}
          <Card className="p-3 sm:p-4">
            <div className="flex items-center justify-between gap-2 mb-3 sm:mb-4">
              <h3 className="text-base sm:text-lg font-semibold">ë³€ë™ ìš”ì•½</h3>
              <Badge variant={netChange >= 0 ? "default" : "destructive"} className="flex items-center gap-1 w-fit">
                {netChange >= 0 ? <TrendingUp size={14} /> : <TrendingDown size={14} />}
                <span className="text-xs sm:text-sm">{netChange >= 0 ? 'ì¦ê°€' : 'ê°ì†Œ'}</span>
