import { Transaction, CashAsset } from '@/types';
import CashChangeDetailModal from '@/components/CashChangeDetailModal';
import { formatCurrency, formatTransactionAmount } from '@/utils/helpers';

interface CashTransactionHistoryProps {
  isOpen: boolean;
  onClose: () => void;
  cashAsset: CashAsset;
  transactions: Transaction[];
}

export default function CashTransactionHistory({ 
  isOpen, 
  onClose, 
  cashAsset, 
  transactions 
}: CashTransactionHistoryProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('timestamp');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  const [typeFilter, setTypeFilter] = useState<'all' | 'increase' | 'decrease' | 'direct' | 'exchange'>('all');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [displayCount, setDisplayCount] = useState<number>(5);
  const [selectedTransaction, setSelectedTransaction] = useState<Transaction | null>(null);
  const [isCashDetailModalOpen, setIsCashDetailModalOpen] = useState(false);
  const [isFilterExpanded, setIsFilterExpanded] = useState(false);
  const titleRef = useRef<HTMLHeadingElement>(null);

  // ëª¨ë‹¬ì´ ì—´ë¦´ ë•Œ í¬ì»¤ìŠ¤ ê´€ë¦¬
  useEffect(() => {
    if (isOpen) {
      // ëª¨ë“  input ìš”ì†Œì—ì„œ í¬ì»¤ìŠ¤ ì œê±°
      const inputs = document.querySelectorAll('input');
      inputs.forEach(input => {
        input.blur();
        // ëª¨ë°”ì¼ì—ì„œ date inputì´ ìë™ìœ¼ë¡œ ì—´ë¦¬ëŠ” ê²ƒì„ ë°©ì§€
        if (input.type === 'date') {
          input.readOnly = true;
          setTimeout(() => {
            input.readOnly = false;
          }, 300);
        }
      });

      // ì œëª©ì— í¬ì»¤ìŠ¤ ì£¼ê¸°
      setTimeout(() => {
        titleRef.current?.focus();
      }, 150);
    }
  }, [isOpen]);

  // í•´ë‹¹ í˜„ê¸ˆ ìì‚°ê³¼ ê´€ë ¨ëœ ê±°ë˜ë§Œ í•„í„°ë§
  const cashTransactions = transactions.filter(transaction => {
    // cash_change ë˜ëŠ” cash_exchange íƒ€ì… ê±°ë˜ í•„í„°ë§
    const isCashTransaction = transaction.type === 'cash_change' || (transaction.type as string) === 'cash_exchange';

    // í˜„ê¸ˆ ìì‚°ëª…ì´ ì •í™•íˆ ì¼ì¹˜í•˜ê±°ë‚˜ í†µí™”ê°€ ì¼ì¹˜í•˜ëŠ” ê²½ìš°
    const fromAssetMatches = transaction.fromAssetName === cashAsset.name || 
                           (transaction.fromAssetName?.includes(cashAsset.currency) && 
                           transaction.fromAssetName?.includes('í˜„ê¸ˆ'));
    const toAssetMatches = transaction.toAssetName === cashAsset.name || 
                         (transaction.toAssetName?.includes(cashAsset.currency) && 
                         transaction.toAssetName?.includes('í˜„ê¸ˆ'));

    // ë©”íƒ€ë°ì´í„°ì— í•´ë‹¹ ìì‚° IDê°€ ìˆëŠ”ì§€ í™•ì¸
    const hasMatchingAssetId = transaction.metadata?.assetId === cashAsset.id;

    return isCashTransaction && (fromAssetMatches || toAssetMatches || hasMatchingAssetId);
  });

  const filteredTransactions = cashTransactions
    .filter(transaction => {
      // Search filter
      const searchLower = searchTerm.toLowerCase();
      const matchesSearch = !searchTerm || 
        transaction.memo?.toLowerCase().includes(searchLower);

      // Date filter
      const transactionDate = new Date(transaction.timestamp);
      const matchesDateRange = (!startDate || transactionDate >= new Date(startDate)) &&
                             (!endDate || transactionDate <= new Date(endDate + 'T23:59:59'));

      // Type filter
      if (typeFilter === 'all') return matchesSearch && matchesDateRange;

      const isDecrease = isDecreaseTransaction(transaction);
      const isDirect = transaction.type === 'cash_change';
      const isExchange = (transaction.type as string) === 'cash_exchange';

      const matchesType = typeFilter === 'increase' ? !isDecrease :
                         typeFilter === 'decrease' ? isDecrease :
                         typeFilter === 'direct' ? isDirect :
                         typeFilter === 'exchange' ? isExchange : true;

      return matchesSearch && matchesDateRange && matchesType;
    })
    .sort((a, b) => {
      if (sortBy === 'timestamp') {
        const dateA = new Date(a.timestamp).getTime();
        const dateB = new Date(b.timestamp).getTime();
        return sortOrder === 'desc' ? dateB - dateA : dateA - dateB;
      } else if (sortBy === 'amount') {
        const amountA = getTransactionAmount(a);
        const amountB = getTransactionAmount(b);
        return sortOrder === 'desc' ? amountB - amountA : amountA - amountB;
      }
      return 0;
    });

  const displayedTransactions = filteredTransactions.slice(0, displayCount);

  // ê±°ë˜ íƒ€ì…ë³„ ê¸ˆì•¡ ê³„ì‚°
  function getTransactionAmount(transaction: Transaction): number {
    if (transaction.type === 'cash_change') {
      // cash_change ê±°ë˜ì˜ ê²½ìš° ê¶Œì¢…ë³„ ë³€ë™ì‚¬í•­ì„ ìš°ì„ í•˜ì—¬ ê³„ì‚°
      const metadata = transaction.metadata as any;
      if (metadata?.denominationChanges) {
        const totalChange = Object.entries(metadata.denominationChanges).reduce((total: number, [denom, change]: [string, any]) => {
          const denomValue = parseFloat(denom.replace(/,/g, ''));
          const changeAmount = typeof change === 'number' ? change : parseFloat(change) || 0;
          return total + (denomValue * changeAmount);
        }, 0);
        return Math.abs(totalChange);
      }
      // ê¶Œì¢…ë³„ ë³€ë™ì‚¬í•­ì´ ì—†ëŠ” ê²½ìš° ë©”íƒ€ë°ì´í„°ì˜ balanceChange ê°’ ì‚¬ìš©
      if (metadata?.balanceChange !== undefined) {
        return Math.abs(metadata.balanceChange);
      }
      // í´ë°±: ê¸°ì¡´ ë¡œì§
      return Math.abs(parseFloat(String(transaction.toAmount)) || 0);
    } else if ((transaction.type as string) === 'cash_exchange') {
      // í™˜ì „ ê±°ë˜ì˜ ê²½ìš° í•´ë‹¹ í˜„ê¸ˆ ìì‚°ê³¼ ì—°ê´€ëœ ê¸ˆì•¡ ë°˜í™˜
      // fromAsset = ê³ ê°ì´ ì¤€ ëˆ, toAsset = ê³ ê°ì´ ë°›ì€ ëˆ
      // VNDâ†’KRW: VND fromAsset, KRW toAsset (í™˜ì „ìƒ KRW ê°ì†Œ)
      // KRWâ†’VND: KRW fromAsset, VND toAsset (í™˜ì „ìƒ KRW ì¦ê°€)
      if (transaction.fromAssetName === cashAsset.name || 
          (transaction.fromAssetName?.includes(cashAsset.currency) && transaction.fromAssetName?.includes('í˜„ê¸ˆ'))) {
        // KRWê°€ fromAssetì¸ ê²½ìš°: ê³ ê°ì´ KRWë¥¼ ì£¼ê³  ë‹¤ë¥¸ í†µí™”ë¥¼ ë°›ìŒ (í™˜ì „ìƒ KRW ì¦ê°€)
        return parseFloat(String(transaction.fromAmount)) || 0;
      } else if (transaction.toAssetName === cashAsset.name || 
                (transaction.toAssetName?.includes(cashAsset.currency) && transaction.toAssetName?.includes('í˜„ê¸ˆ'))) {
        // KRWê°€ toAssetì¸ ê²½ìš°: ê³ ê°ì´ ë‹¤ë¥¸ í†µí™”ë¥¼ ì£¼ê³  KRWë¥¼ ë°›ìŒ (í™˜ì „ìƒ KRW ê°ì†Œ)
        return parseFloat(String(transaction.toAmount)) || 0;
      }
    }
    return 0;
  }

  // ê±°ë˜ê°€ ê°ì†Œì¸ì§€ í™•ì¸
  function isDecreaseTransaction(transaction: Transaction): boolean {
    if (transaction.type === 'cash_change') {
      // cash_change ê±°ë˜ì˜ ê²½ìš° ê¶Œì¢…ë³„ ë³€ë™ì‚¬í•­ì„ ìš°ì„ í•˜ì—¬ íŒë‹¨
      const metadata = transaction.metadata as any;
      if (metadata?.denominationChanges) {
        const totalChange = Object.entries(metadata.denominationChanges).reduce((total: number, [denom, change]: [string, any]) => {
          const denomValue = parseFloat(denom.replace(/,/g, ''));
          const changeAmount = typeof change === 'number' ? change : parseFloat(change) || 0;
          return total + (denomValue * changeAmount);
        }, 0);
        return totalChange < 0;
      }
      // ê¶Œì¢…ë³„ ë³€ë™ì‚¬í•­ì´ ì—†ëŠ” ê²½ìš° ë©”íƒ€ë°ì´í„°ì˜ balanceChange ê°’ìœ¼ë¡œ íŒë‹¨
      if (metadata?.balanceChange !== undefined) {
        return metadata.balanceChange < 0;
      }
      // í´ë°±: ê¸°ì¡´ ë¡œì§
      return (parseFloat(String(transaction.toAmount)) || 0) < (parseFloat(String(transaction.fromAmount)) || 0);
    } else if ((transaction.type as string) === 'cash_exchange') {
      // í™˜ì „ ê±°ë˜ì—ì„œ ì¦ê°€/ê°ì†Œ íŒë‹¨
      // fromAsset = ê³ ê°ì´ ì¤€ ëˆ (í™˜ì „ìƒ ì…ì¥ì—ì„œ ì¦ê°€)
      // toAsset = ê³ ê°ì´ ë°›ì€ ëˆ (í™˜ì „ìƒ ì…ì¥ì—ì„œ ê°ì†Œ)
      // VNDâ†’KRW: VND fromAsset, KRW toAsset (í™˜ì „ìƒ KRW ê°ì†Œ)
      // KRWâ†’VND: KRW fromAsset, VND toAsset (í™˜ì „ìƒ KRW ì¦ê°€)
      return transaction.toAssetName === cashAsset.name || 
             (transaction.toAssetName?.includes(cashAsset.currency) && transaction.toAssetName?.includes('í˜„ê¸ˆ'));
    }
    return false;
  }

  // ê±°ë˜ íƒ€ì… í…ìŠ¤íŠ¸ ìƒì„±
  function getTransactionTypeText(transaction: Transaction, isDecrease: boolean): string {
    if (transaction.type === 'cash_change') {
      return isDecrease ? `${cashAsset.currency} í˜„ê¸ˆ ì§ì ‘ ê°ì†Œ` : `${cashAsset.currency} í˜„ê¸ˆ ì§ì ‘ ì¦ê°€`;
    } else if ((transaction.type as string) === 'cash_exchange') {
      // í™˜ì „ ê±°ë˜ì—ì„œ ìì„¸í•œ ì •ë³´ í‘œì‹œ
      const metadata = transaction.metadata as any;
      const fromCurrency = transaction.fromAssetName?.includes('KRW') ? 'KRW' : 
                          transaction.fromAssetName?.includes('VND') ? 'VND' : 
                          transaction.fromAssetName?.includes('USD') ? 'USD' : '';
      const toCurrency = transaction.toAssetName?.includes('KRW') ? 'KRW' : 
                        transaction.toAssetName?.includes('VND') ? 'VND' : 
                        transaction.toAssetName?.includes('USD') ? 'USD' : '';

      // í™˜ì „ ë°©í–¥ê³¼ ê¶Œì¢… ì •ë³´ ì¶”ì¶œ
      let denominationInfo = '';
      if (metadata?.denominationAmounts) {
        const denoms = Object.entries(metadata.denominationAmounts);
        if (denoms.length > 0) {
          const [denom, count] = denoms[0] as [string, string];
          if (fromCurrency === 'KRW') {
            denominationInfo = ` ${parseInt(denom).toLocaleString()}ì›ê¶Œ ${count}ì¥`;
          } else if (fromCurrency === 'VND') {
            denominationInfo = ` ${parseInt(denom).toLocaleString()}ë™ê¶Œ ${count}ì¥`;
          } else if (fromCurrency === 'USD') {
            denominationInfo = ` $${denom} ${count}ì¥`;
          }
        }
      }

      if (isDecrease) {
        return `${cashAsset.currency} í˜„ê¸ˆ í™˜ì „ ì§€ê¸‰`;
      } else {
        return `${cashAsset.currency} í˜„ê¸ˆ í™˜ì „ ìˆ˜ë ¹`;
      }
    }
    return 'ê¸°íƒ€ ê±°ë˜';
  }

  const handleTransactionClick = (transaction: Transaction) => {
    setSelectedTransaction(transaction);
    setIsCashDetailModalOpen(true);
  };

  if (!isOpen) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="w-[98vw] sm:w-[95vw] md:w-[90vw] lg:w-[85vw] xl:w-[80vw] max-w-6xl h-[95vh] flex flex-col p-3 sm:p-4 md:p-6">
        {/* ê³ ì • í—¤ë” */}
        <div className="flex-shrink-0">
          <DialogHeader>
            <DialogTitle 
              ref={titleRef}
              tabIndex={-1}
              className="text-lg sm:text-xl font-bold outline-none text-center sm:text-left"
            >
              {cashAsset.currency} í˜„ê¸ˆ ì¦ê° ë‚´ì—­
            </DialogTitle>
          </DialogHeader>

          {/* í˜„ê¸ˆ ìì‚° ì •ë³´ - PC/ëª¨ë°”ì¼ í†µí•© */}
          <Card className="p-2 sm:p-3 md:p-4 bg-gray-50 mt-2 sm:mt-4">
            <div className="flex justify-between items-center">
              <div>
                <h3 className="font-semibold text-sm sm:text-base md:text-lg">{cashAsset.name}</h3>
                <p className="text-xs text-gray-600">í˜„ì¬ ì”ì•¡</p>
              </div>
              <div className="text-right">
                <p className="text-lg sm:text-xl md:text-2xl font-bold text-gray-900 break-words">
                  {formatCurrency(cashAsset.balance, cashAsset.currency)} {cashAsset.currency}
                </p>
              </div>
            </div>
          </Card>
        </div>

        {/* ê²€ìƒ‰ ë° í•„í„° - ê³ ì • ì˜ì—­ */}
        <div className="flex-shrink-0 mt-2 sm:mt-4">
          <Card className="p-2 sm:p-3 md:p-4">
            {/* í•„í„° í† ê¸€ í—¤ë” */}
            <div className="flex items-center justify-between mb-2 sm:mb-3">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsFilterExpanded(!isFilterExpanded)}
                className="flex items-center gap-2 p-2 hover:bg-gray-100"
              >
                <Filter size={14} />
                <span className="text-sm font-medium">ê²€ìƒ‰ ë° í•„í„°</span>
                {isFilterExpanded ? <ChevronUp size={14} /> : <ChevronDown size={14} />}
              </Button>
            </div>

            {/* ê²€ìƒ‰ ë° í•„í„° ë‚´ìš© - ì ‘ê³  í¼ ìˆ˜ ìˆìŒ */}
            {isFilterExpanded && (
              <div className="space-y-2 sm:space-y-3">
                {/* í•„í„° ë° ì •ë ¬ - ëª¨ë°”ì¼ ìµœì í™” */}
                <div className="space-y-2 sm:space-y-3">
                  {/* ë‚ ì§œ í•„í„° - ëª¨ë°”ì¼ì—ì„œ ì„¸ë¡œ ë°°ì¹˜ */}
                  <div className="space-y-2">
                    <div className="flex items-center gap-1 text-sm text-gray-600">
                      <Calendar size={14} />
                      <span>ê¸°ê°„</span>
                    </div>
                    <div className="flex gap-2 items-center">
                      <Input
                        type="date"
                        placeholder="ì‹œì‘ì¼"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                        className="flex-1 text-xs sm:text-sm h-9 sm:h-9"
                        data-testid="input-start-date"
                        onFocus={(e) => {
                          // ëª¨ë°”ì¼ì—ì„œ ì¦‰ì‹œ ë‹«ê¸° ë°©ì§€
                          e.target.showPicker && e.target.showPicker();
                        }}
                        onBlur={(e) => {
                          // í¬ì»¤ìŠ¤ ìƒì„ ë•Œ ì²˜ë¦¬
                          e.target.blur();
                        }}
                      />
                      <span className="text-gray-500 text-sm px-1">~</span>
                      <Input
                        type="date"
                        placeholder="ì¢…ë£Œì¼"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                        className="flex-1 text-xs sm:text-sm h-9 sm:h-9"
                        data-testid="input-end-date"
                        onFocus={(e) => {
                          // ëª¨ë°”ì¼ì—ì„œ ì¦‰ì‹œ ë‹«ê¸° ë°©ì§€
                          e.target.showPicker && e.target.showPicker();
                        }}
                        onBlur={(e) => {
                          // í¬ì»¤ìŠ¤ ìƒì„ ë•Œ ì²˜ë¦¬
                          e.target.blur();
                        }}
                      />
                    </div>
                  </div>

                  {/* íƒ€ì… í•„í„° ë° ì •ë ¬ - ëª¨ë°”ì¼ í•œ ì¤„ ë°°ì¹˜ */}
                  <div className="flex gap-2">
                    <Select value={typeFilter} onValueChange={(value: 'all' | 'increase' | 'decrease' | 'direct' | 'exchange') => setTypeFilter(value)}>
                      <SelectTrigger className="w-1/3 h-9 text-xs sm:text-sm">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">ì „ì²´</SelectItem>
                        <SelectItem value="increase">ì¦ê°€</SelectItem>
                        <SelectItem value="decrease">ê°ì†Œ</SelectItem>
                        <SelectItem value="direct">ì§ì ‘ ì¦ê°</SelectItem>
                        <SelectItem value="exchange">í™˜ì „ ê±°ë˜</SelectItem>
                      </SelectContent>
                    </Select>

                    <Select value={sortBy} onValueChange={setSortBy}>
                      <SelectTrigger className="flex-1 h-9 text-xs sm:text-sm">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="timestamp">ì‹œê°„ìˆœ</SelectItem>
                        <SelectItem value="amount">ê¸ˆì•¡ìˆœ</SelectItem>
                      </SelectContent>
                    </Select>

                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                      data-testid="button-sort-order"
                      className="px-2 sm:px-3 h-9"
                    >
                      <ArrowUpDown size={14} />
                    </Button>
                  </div>

                  {/* ê²€ìƒ‰ì–´ ì…ë ¥ê³¼ í•„í„° ì´ˆê¸°í™” ë²„íŠ¼ - ê°™ì€ ì¤„ ë°°ì¹˜ */}
                  <div className="flex gap-2">
                    <div className="flex-1">
                      <div className="relative">
                        <Search className="absolute left-2 sm:left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
                        <Input
                          placeholder="ê±°ë˜ë‚´ì—­ ê²€ìƒ‰"
                          value={searchTerm}
                          onChange={(e) => setSearchTerm(e.target.value)}
                          className="pl-8 sm:pl-9 text-sm h-9"
                          data-testid="input-search-transactions"
                          autoFocus={false}
                        />
                      </div>
                    </div>

                    {/* í•„í„° ì´ˆê¸°í™” ë²„íŠ¼ - ê²€ìƒ‰ê³¼ ê°™ì€ ì¤„ */}
                    {(searchTerm || startDate || endDate || typeFilter !== 'all') && (
                      <Button
