import { TrendingUp, TrendingDown, ArrowRight, X, Banknote } from 'lucide-react';
import { Transaction } from '@/types';
import { formatInputWithCommas, getCurrencyDisplayName } from '@/utils/helpers';
import { useQuery } from '@tanstack/react-query';

interface TransactionDetailModalProps {
  transaction: Transaction | null;
  isOpen: boolean;
  onClose: () => void;
}

export default function TransactionDetailModal({ 
  transaction, 
  isOpen, 
  onClose 
}: TransactionDetailModalProps) {
  // ê´€ë ¨ ê±°ë˜ë“¤ ì¡°íšŒ (ê°™ì€ ì‹œê°„ëŒ€ì˜ cash_change ê±°ë˜ë“¤)
  const { data: allTransactions } = useQuery({
    queryKey: ['/api/transactions'],
    enabled: isOpen && !!transaction
  });

  const getRelatedTransactions = () => {
    if (!transaction || !allTransactions || !Array.isArray(allTransactions)) return [];

    // ë©”ì¸ ê±°ë˜ì™€ ì§ì ‘ ì—°ê´€ëœ cash_change ê±°ë˜ë“¤ë§Œ ì°¾ìŒ
    const transactionTime = new Date(transaction.timestamp).getTime();
    const timeWindow = 5000; // 5ì´ˆë¡œ ì¶•ì†Œí•˜ì—¬ ë” ì •í™•í•œ ì—°ê´€ì„± í™•ë³´

    return allTransactions.filter((t: Transaction) => {
      if (t.id === transaction.id) return false; // ìê¸° ìì‹  ì œì™¸
      if (t.type !== 'cash_change') return false; // cash_changeë§Œ

      const tTime = new Date(t.timestamp).getTime();
      const timeDiff = Math.abs(transactionTime - tTime);

      // ì‹œê°„ ìœˆë„ìš° ë‚´ì— ìˆê³ , ê±°ë˜ì™€ ê´€ë ¨ëœ ìì‚°ëª…ì´ ì¼ì¹˜í•˜ëŠ” ê²½ìš°ë§Œ
      if (timeDiff <= timeWindow) {
        const isFromAssetRelated = t.fromAssetName === transaction.fromAssetName || t.toAssetName === transaction.fromAssetName;
        const isToAssetRelated = t.fromAssetName === transaction.toAssetName || t.toAssetName === transaction.toAssetName;
        return isFromAssetRelated || isToAssetRelated;
      }

      return false;
    });
  };

  const relatedTransactions = getRelatedTransactions();

  const getTransactionTypeText = (type: string) => {
    switch (type) {
      case 'cash_exchange':
        return 'í˜„ê¸ˆ í™˜ì „';
      case 'cash_to_krw_account':
        return 'í˜„ê¸ˆâ†’KRWê³„ì¢Œ';
      case 'cash_to_vnd_account':
        return 'í˜„ê¸ˆâ†’VNDê³„ì¢Œ';
      case 'vnd_account_to_krw_account':
        return 'VNDê³„ì¢Œâ†’KRWê³„ì¢Œ';
      case 'krw_account_to_vnd_account':
        return 'KRWê³„ì¢Œâ†’VNDê³„ì¢Œ';
      case 'cash_change':
        return 'í˜„ê¸ˆ ì¦ê°';
      default:
        return type;
    }
  };

  const formatDateTime = (timestamp: any) => {
    const date = new Date(timestamp);
    return date.toLocaleString('ko-KR', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  if (!transaction) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] flex flex-col [&>button]:hidden">
        <DialogHeader className="sticky top-0 bg-white dark:bg-gray-950 z-10 py-1.5 border-b border-gray-200 dark:border-gray-700 flex-shrink-0">
          <div className="flex items-center justify-between px-1">
            <DialogTitle className="flex items-center gap-2">
              <Badge variant="outline" className="px-2 py-1 text-xs">
                {getTransactionTypeText(transaction.type)}
              </Badge>
              <span className="text-base">ê±°ë˜ ìƒì„¸</span>
            </DialogTitle>
            <button
              onClick={onClose}
              className="rounded-sm opacity-70 ring-offset-background transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none"
            >
              <X className="h-4 w-4" />
              <span className="sr-only">Close</span>
            </button>
          </div>
        </DialogHeader>

        <div className="flex-1 overflow-y-auto">
          <div className="space-y-3 mt-3">
          {/* ë©”ì¸ ê±°ë˜ ì •ë³´ */}
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-base">ê±°ë˜ ì •ë³´</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2 pt-0">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                <div className="space-y-1">
                  <div className="text-xs text-gray-500">ê±°ë˜ ID</div>
                  <div className="font-mono text-xs bg-gray-100 dark:bg-gray-800 p-2 rounded">
                    {transaction.id}
                  </div>
                </div>
                <div className="space-y-1">
                  <div className="text-xs text-gray-500">ê±°ë˜ ì‹œê°„</div>
                  <div className="font-medium text-xs">
                    {formatDateTime(transaction.timestamp)}
                  </div>
                </div>
              </div>

              <Separator />

              {/* ì¶œê¸ˆ â†’ ì…ê¸ˆ â†’ í™˜ìœ¨ì„ í•œ ì¤„ì— í‘œì‹œ */}
              <div className="flex items-center justify-between py-1">
                <div className="text-center flex-1">
                  <div className="text-xs text-gray-500 mb-1">ì¶œê¸ˆ</div>
                  <div className="font-semibold text-sm">
                    {formatInputWithCommas(Math.floor(parseFloat(transaction.fromAmount.toString())).toString())} {(() => {
                      if (transaction.fromAssetName.includes('USD')) return 'ë‹¬ëŸ¬';
                      if (transaction.fromAssetName.includes('KRW')) return 'ì›';
                      if (transaction.fromAssetName.includes('VND')) return 'ë™';
                      return '';
                    })()}
                  </div>
