                            {(transaction.metadata as any).customerAccount.accountHolder}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </>
              )}
            </CardContent>
          </Card>

          {/* ìì‚° ë³€ë™ ë‚´ì—­ - í˜„ê¸ˆ í™˜ì „ ê±°ë˜ì—ë§Œ í‘œì‹œ */}
          {relatedTransactions.length > 0 && transaction.type === 'cash_exchange' && (
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-lg">ìì‚° ë³€ë™ ë‚´ì—­</CardTitle>
                <div className="text-sm text-gray-500">
                  ì´ ê±°ë˜ë¡œ ì¸í•œ í˜„ê¸ˆ ìì‚° ë³€ë™ì‚¬í•­
                </div>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="space-y-2">
                  {relatedTransactions.map((relatedTx: Transaction) => (
                    <div key={relatedTx.id} className="border rounded-lg bg-gray-50 dark:bg-gray-800 relative flex">
                      {/* ì¢Œì¸¡: í° ê¸ˆì•¡ í‘œì‹œ (ì„¸ë¡œ ì¤‘ì•™ ì •ë ¬) */}
                      <div className="flex items-center justify-center py-3 px-2 flex-shrink-0 border-r border-gray-200 dark:border-gray-600 min-h-[80px]">
                        <div className="text-center">
                          <div className={`font-bold text-lg ${
                            parseFloat(relatedTx.toAmount.toString()) > parseFloat(relatedTx.fromAmount.toString()) 
                              ? 'text-green-600' 
                              : 'text-red-600'
                          }`}>
                            {parseFloat(relatedTx.toAmount.toString()) > parseFloat(relatedTx.fromAmount.toString()) ? '+' : ''}
                            {formatInputWithCommas(Math.abs(parseFloat(relatedTx.toAmount.toString()) - parseFloat(relatedTx.fromAmount.toString())).toString())}
                            {(() => {
                              if (relatedTx.toAssetName.includes('USD')) return 'ë‹¬ëŸ¬';
                              if (relatedTx.toAssetName.includes('VND')) return 'ë™';
                              if (relatedTx.toAssetName.includes('KRW')) return 'ì›';
                              return '';
                            })()}
                          </div>
                          <div className="flex items-center justify-center gap-1 mt-1">
                            {parseFloat(relatedTx.toAmount.toString()) > parseFloat(relatedTx.fromAmount.toString()) ? (
                              <TrendingUp className="text-green-600 flex-shrink-0" size={12} />
                            ) : (
                              <TrendingDown className="text-red-600 flex-shrink-0" size={12} />
                            )}
                            <span className="font-medium text-xs text-gray-600">
                              {relatedTx.toAssetName}
                            </span>
                          </div>
                        </div>
                      </div>

                      {/* ìš°ì¸¡: ê¶Œì¢…ë³„ ë³€ë™ */}
                      <div className="flex-1 p-2">
                        {relatedTx.metadata && typeof relatedTx.metadata === 'object' && relatedTx.metadata !== null && 'denominationChanges' in relatedTx.metadata && (
                          <div>
                            <div className="text-xs text-gray-600 dark:text-gray-400 mb-1">ê¶Œì¢…ë³„ ë³€ë™:</div>
                            <div className="flex flex-wrap gap-1">
                              {Object.entries(relatedTx.metadata.denominationChanges as Record<string, number>)
                                .filter(([_, count]) => count !== 0)
                                .sort(([a], [b]) => parseInt(b) - parseInt(a)) // ê³ ì•¡ê¶Œë¶€í„° ì •ë ¬
                                .map(([denom, count]) => (
                                  <div key={denom} className={`text-xs px-1 py-0.5 rounded border whitespace-nowrap ${
                                    count > 0 
                                      ? 'bg-green-50 text-green-700 border-green-200 dark:bg-green-900/30 dark:text-green-400 dark:border-green-600' 
                                      : 'bg-red-50 text-red-700 border-red-200 dark:bg-red-900/30 dark:text-red-400 dark:border-red-600'
                                  }`}>
                                    {formatInputWithCommas(denom)}{(() => {
                                      if (relatedTx.toAssetName.includes('USD')) return 'ë‹¬ëŸ¬';
                                      if (relatedTx.toAssetName.includes('VND')) return 'ë™';
                                      if (relatedTx.toAssetName.includes('KRW')) return 'ì›';
                                      return '';
                                    })()} Ã— {count > 0 ? '+' : ''}{count}
                                  </div>
                                ))}
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

