};

// ìë™ ê±°ë˜ ìœ í˜• ê²°ì • í•¨ìˆ˜
const determineTransactionType = (fromCurrency: string, toCurrency: string): string => {
  // ê¸°ë³¸ì ìœ¼ë¡œ í˜„ê¸ˆ í™˜ì „
  return "cash_exchange";
};

export default function TransactionForm() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // ìƒíƒœ ê´€ë¦¬
  const [formData, setFormData] = useState({
    transactionType: determineTransactionType("KRW", "VND"),
    fromCurrency: "KRW",
    toCurrency: "VND",
    fromDenominations: [] as string[], // ì—¬ëŸ¬ ê¶Œì¢… ì„ íƒ
    toDenomination: "",
    denominationAmounts: {} as Record<string, string>, // ê¶Œì¢…ë³„ ìˆ˜ëŸ‰
    vndBreakdown: {} as Record<string, number>, // VND ê¶Œì¢…ë³„ ë¶„ë°°
    fromAmount: "",
    toAmount: "",
    exchangeRate: "",
    customerName: "",
    customerPhone: "",
    memo: "",
