  });

  // VND ê¶Œì¢…ë³„ ë¶„ë°° ìˆ˜ì •ìš© ìƒíƒœ
  const [vndBreakdown, setVndBreakdown] = useState<Record<string, number>>({});

  // VND ë¶„ë°° ê¸°ì¤€ ê¸ˆì•¡ (ê¶Œì¢… ì ‘ê¸°ì™€ ë…ë¦½ì )
  const [vndBaseAmount, setVndBaseAmount] = useState<number>(0);

  // KRW ê¶Œì¢…ë³„ ë¶„ë°° ìˆ˜ì •ìš© ìƒíƒœ
  const [krwBreakdown, setKrwBreakdown] = useState<Record<string, number>>({});

  // USD ê¶Œì¢…ë³„ ë¶„ë°° ìˆ˜ì •ìš© ìƒíƒœ
  const [usdBreakdown, setUsdBreakdown] = useState<Record<string, number>>({});

  // í†µí™”ë‚˜ ê¶Œì¢… ë³€ê²½ ì‹œ ë¶„ë°° ìƒíƒœ ì´ˆê¸°í™”
  useEffect(() => {
    setVndBreakdown({});
    setKrwBreakdown({});
    setUsdBreakdown({});
  }, [formData.fromCurrency, formData.toCurrency, JSON.stringify(formData.denominationAmounts)]);

  // ê¶Œì¢…ë³„ í™˜ìœ¨ì˜ í‰ê·  ê³„ì‚° (í™˜ì „ìƒ ì‹œì„¸ ì ìš©)
  const calculateAverageExchangeRate = () => {
    try {
      // ë§¤ìˆ˜/ë§¤ë„ íƒ€ì… ìë™ íŒë³„
      const rateType = determineTransactionRateType(formData.transactionType, formData.fromCurrency, formData.toCurrency);

      // í™˜ìœ¨ ìŒ ê²°ì •
      const ratePair = getExchangeRatePair(formData.fromCurrency, formData.toCurrency);

      if (!Array.isArray(exchangeRates)) {
        console.warn('í™˜ìœ¨ ë°ì´í„°ê°€ ë°°ì—´ì´ ì•„ë‹™ë‹ˆë‹¤');
        return parseFloat(formData.exchangeRate) || 0;
      }

      // í•´ë‹¹ í†µí™”ìŒì˜ ëª¨ë“  í™œì„± í™˜ìœ¨ ì¡°íšŒ
      const validRates = exchangeRates.filter((rate: any) => 
        rate.fromCurrency === ratePair.fromCurrency && 
        rate.toCurrency === ratePair.toCurrency &&
        (rate.isActive === true || rate.isActive === 'true')
      );

      if (validRates.length === 0) {
        console.warn(`í™œì„± í™˜ìœ¨ ì—†ìŒ: ${ratePair.fromCurrency} â†’ ${ratePair.toCurrency}`);
        return parseFloat(formData.exchangeRate) || 0;
      }

      // ë§¤ìˆ˜/ë§¤ë„ì— ë”°ë¼ ì ì ˆí•œ í™˜ìœ¨ ì„ íƒí•˜ì—¬ í‰ê·  ê³„ì‚°
      let totalRate = 0;
      let validCount = 0;

      validRates.forEach((rate: any) => {
        const rateValue = rateType === 'buy' 
          ? parseFloat(rate.myBuyRate || '0')
          : parseFloat(rate.mySellRate || '0');

        if (rateValue > 0) {
          totalRate += rateValue;
          validCount++;
        }
      });

      if (validCount === 0) {
        console.warn(`ìœ íš¨í•œ ${rateType} í™˜ìœ¨ ì—†ìŒ: ${ratePair.fromCurrency} â†’ ${ratePair.toCurrency}`);
        return parseFloat(formData.exchangeRate) || 0;
      }

      let avgRate = totalRate / validCount;

      // í™˜ìœ¨ ë°©í–¥ ì¡°ì • (í•„ìš”í•œ ê²½ìš° ì—­í™˜ìœ¨ ê³„ì‚°)
      if (ratePair.fromCurrency !== formData.fromCurrency) {
        avgRate = 1 / avgRate;
      }

      console.log(`í™˜ì „ìƒ í‰ê·  ì‹œì„¸ ê³„ì‚°: ${formData.fromCurrency}â†’${formData.toCurrency} (${rateType}) = ${avgRate} (${validCount}ê°œ í™˜ìœ¨ í‰ê· )`);
      return avgRate;

    } catch (error) {
      console.error('í‰ê·  í™˜ìœ¨ ê³„ì‚° ì¤‘ ì˜¤ë¥˜:', error);

      // ê¸°ì¡´ ë¡œì§: ì‹¤ì œ ê±°ë˜ ê¸ˆì•¡ ê¸°ì¤€ ê³„ì‚° (fallback)
      const totalFromAmount = calculateTotalFromAmount();
      const totalToAmount = parseFloat(formData.toAmount);

      if (totalFromAmount > 0 && totalToAmount > 0) {
        return totalToAmount / totalFromAmount;
      }
      return parseFloat(formData.exchangeRate) || 0;
    }
  };

  // VND ì›ë³¸ ê³„ì‚°ê°’ ì €ì¥ (ë‚´ë¦¼ ì „)
  const [vndOriginalAmount, setVndOriginalAmount] = useState<number>(0);

  const [calculatedData, setCalculatedData] = useState({
    exchangeRate: 0,
    rateSource: "",
    isAutoCalculated: false
  });

  // ìì‚° ëª©ë¡ ì¡°íšŒ (ì‹¤ì‹œê°„ ìƒˆë¡œê³ ì¹¨)
  const { data: assets = [], isLoading: isLoadingAssets, refetch: refetchAssets } = useQuery<Asset[]>({
    queryKey: ["/api/assets"],
    refetchOnWindowFocus: true,
    refetchInterval: 10000, // 10ì´ˆë§ˆë‹¤ ìë™ ìƒˆë¡œê³ ì¹¨
  });

  // í™˜ìœ¨ ëª©ë¡ ì¡°íšŒ
  const { data: exchangeRates = [], isLoading: isLoadingRates } = useQuery<any[]>({
    queryKey: ["/api/exchange-rates"],
  });

  // ê¶Œì¢…ë³„ VND â†’ KRW ë§¤ë§¤ì‹œì„¸ ê³„ì‚° í•¨ìˆ˜
  const getVndToKrwDisplayRate = useCallback((denomination: string) => {
    if (formData.fromCurrency === "VND" && formData.toCurrency === "KRW" && Array.isArray(exchangeRates)) {
      // ìš°ì„  í•´ë‹¹ ê¶Œì¢…ì˜ VND â†’ KRW í™˜ìœ¨ ì¡°íšŒ
      let specificRate = exchangeRates.find((rate: any) => 
        rate.fromCurrency === "VND" && 
        rate.toCurrency === "KRW" && 
        rate.denomination === denomination &&
        rate.isActive === "true"
      );

      // í•´ë‹¹ ê¶Œì¢… í™˜ìœ¨ì´ ì—†ìœ¼ë©´ 500000 ê¶Œì¢… í™˜ìœ¨ì„ ëŒ€í‘œê°’ìœ¼ë¡œ ì‚¬ìš©
      if (!specificRate) {
        specificRate = exchangeRates.find((rate: any) => 
          rate.fromCurrency === "VND" && 
          rate.toCurrency === "KRW" && 
          rate.denomination === "500000" &&
          rate.isActive === "true"
        );
      }

      if (specificRate) {
        const vndToKrwBuyRate = parseFloat(specificRate.myBuyRate);
        console.log(`ê¶Œì¢…ë³„ VNDâ†’KRW ë§¤ë§¤ì‹œì„¸ (${denomination}: ${vndToKrwBuyRate}) - ë‚´ ë§¤ì…ê°€ ì‚¬ìš©`);
        return vndToKrwBuyRate;
      } else {
        console.log(`ê¶Œì¢… ${denomination}ì˜ VNDâ†’KRW í™˜ìœ¨ ì •ë³´ ì—†ìŒ`);
        return 0;
      }
    }
    return 0;
  }, [formData.fromCurrency, formData.toCurrency, exchangeRates]);

  // í™˜ì „ìƒ ì‹œì„¸ ì¡°íšŒ (ìë™ í™˜ìœ¨ ì ìš©ìš©)
  const fetchExchangeRate = async (fromCurrency: string, toCurrency: string, denomination: string, transactionType: 'buy' | 'sell') => {
    try {
      const response = await fetch(
        `/api/exchange-rates/transaction?fromCurrency=${fromCurrency}&toCurrency=${toCurrency}&denomination=${denomination}&transactionType=${transactionType}`
      );
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("í™˜ìœ¨ ì¡°íšŒ ì‹¤íŒ¨:", error);
      return null;
    }
  };

  // ê±°ë˜ ìƒì„± mutation
  const createTransactionMutation = useMutation({
    mutationFn: (transactionData: any) => apiRequest("POST", "/api/transactions", transactionData),
    onSuccess: () => {
      toast({
        title: "ìƒˆê±°ë˜ ì²˜ë¦¬ ì™„ë£Œ",
        description: "ê±°ë˜ê°€ ì„±ê³µì ìœ¼ë¡œ ì²˜ë¦¬ë˜ì—ˆìŠµë‹ˆë‹¤.",
      });
      // ê°•ì œ ìºì‹œ ë¬´íš¨í™” ë° ìƒˆë¡œê³ ì¹¨
      queryClient.removeQueries({ queryKey: ["/api/transactions"] });
      queryClient.removeQueries({ queryKey: ["/api/assets"] });
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/assets"] });

      // í¼ ì´ˆê¸°í™”
      setFormData({
        transactionType: "cash_exchange",
        fromCurrency: "KRW",
        toCurrency: "VND",
        fromDenominations: [],
        toDenomination: "",
        denominationAmounts: {},
