      // ê¶Œì¢…ë³„ ë¶„ë°° ì´ˆê¸°í™”
      setVndBreakdown({});
      setKrwBreakdown({});
      setVndOriginalAmount(0);
      setVndBaseAmount(0);
    },
    onError: (error: any) => {
