      toast({
        variant: "destructive",
        title: "ê±°ë˜ ì²˜ë¦¬ ì‹¤íŒ¨",
        description: error?.response?.data?.error || "ê±°ë˜ ì²˜ë¦¬ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.",
      });
    }
  });

  // ê¶Œì¢…ë³„ ê°€ì¤‘í‰ê·  í™˜ìœ¨ ìë™ ì ìš©
  const handleWeightedExchangeRate = async () => {
    if (!formData.fromCurrency || !formData.toCurrency) {
      toast({
        variant: "destructive",
        title: "í™˜ìœ¨ ì¡°íšŒ ì‹¤íŒ¨",
        description: "í†µí™”ë¥¼ ë¨¼ì € ì„ íƒí•˜ì„¸ìš”.",
      });
      return;
    }

    try {
      // ì…ë ¥ëœ ê¶Œì¢…ë³„ ìˆ˜ëŸ‰ í™•ì¸
      const denominationAmounts = Object.fromEntries(
        Object.entries(formData.denominationAmounts).map(([denom, qty]) => [
          denom, 
          parseFloat(qty as string) || 0
        ])
      );

      const hasValidAmounts = Object.values(denominationAmounts).some(amount => amount > 0);

      if (!hasValidAmounts) {
        toast({
          variant: "destructive",
          title: "ê¶Œì¢…ë³„ í™˜ìœ¨ ì¡°íšŒ ì‹¤íŒ¨",
          description: "ê¶Œì¢…ë³„ ìˆ˜ëŸ‰ì„ ë¨¼ì € ì…ë ¥í•˜ì„¸ìš”.",
        });
        return;
      }

      // ìƒˆë¡œìš´ ê¶Œì¢…ë³„ ê°€ì¤‘í‰ê·  í™˜ìœ¨ ê³„ì‚° í•¨ìˆ˜ ì‚¬ìš©
      const weightedRateResult = await calculateWeightedExchangeRate(
        formData.transactionType,
        formData.fromCurrency,
        formData.toCurrency,
        denominationAmounts
      );

      if (weightedRateResult) {
        setCalculatedData({
          exchangeRate: weightedRateResult.rate,
          rateSource: weightedRateResult.source,
          isAutoCalculated: true
        });
        setFormData({ ...formData, exchangeRate: weightedRateResult.rate.toString() });

        toast({
          title: "ê¶Œì¢…ë³„ í™˜ìœ¨ ì ìš© ì™„ë£Œ",
          description: `${weightedRateResult.source}`,
        });
      } else {
        toast({
          variant: "destructive",
          title: "ê¶Œì¢…ë³„ í™˜ìœ¨ ì¡°íšŒ ì‹¤íŒ¨",
          description: "í•´ë‹¹ ì¡°ê±´ì˜ ê¶Œì¢…ë³„ í™˜ìœ¨ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.",
        });
      }
    } catch (error) {
      console.error('ê¶Œì¢…ë³„ ê°€ì¤‘í‰ê·  í™˜ìœ¨ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜:', error);
      toast({
        variant: "destructive",
        title: "ê¶Œì¢…ë³„ í™˜ìœ¨ ì¡°íšŒ ì‹¤íŒ¨",
        description: "ê¶Œì¢…ë³„ í™˜ìœ¨ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.",
      });
    }
  };

  // ìë™ í™˜ìœ¨ ì ìš© (ê¸°ë³¸ í™˜ìœ¨)
  const handleAutoExchangeRate = async () => {
    if (!formData.fromCurrency || !formData.toCurrency) {
      toast({
        variant: "destructive",
        title: "í™˜ìœ¨ ì¡°íšŒ ì‹¤íŒ¨",
        description: "í†µí™”ë¥¼ ë¨¼ì € ì„ íƒí•˜ì„¸ìš”.",
      });
      return;
    }

    try {
      // ì„ íƒëœ ê¶Œì¢… ì¤‘ ì²« ë²ˆì§¸ë¥¼ ê¸°ì¤€ìœ¼ë¡œ í™˜ìœ¨ ì¡°íšŒ
      const denomination = formData.fromDenominations.length > 0 ? formData.fromDenominations[0] : "50000";

      // ìƒˆë¡œìš´ í™˜ì „ìƒ ì‹œì„¸ ì¡°íšŒ í•¨ìˆ˜ ì‚¬ìš©
      const rateResult = await getExchangeShopRate(
        formData.transactionType,
        formData.fromCurrency,
        formData.toCurrency,
        denomination
      );

      if (rateResult) {
