        toast({
          title: "í™˜ìœ¨ ì ìš© ì™„ë£Œ",
          description: `${rateResult.source} í™˜ìœ¨ì´ ì ìš©ë˜ì—ˆìŠµë‹ˆë‹¤.`,
        });
      } else {
        // fallback: ê¸°ì¡´ í‰ê·  í™˜ìœ¨ ê³„ì‚° ì‚¬ìš©
        const avgRate = calculateAverageExchangeRate();
        if (avgRate > 0) {
          setCalculatedData({
            exchangeRate: avgRate,
            rateSource: "í™˜ì „ìƒ í‰ê·  ì‹œì„¸",
            isAutoCalculated: true
          });
          setFormData({ ...formData, exchangeRate: avgRate.toString() });

