                <SelectItem value="bank_to_exchange">ì€í–‰â†’ê±°ë˜ì†Œ</SelectItem>
                <SelectItem value="exchange_purchase">ì½”ì¸ êµ¬ë§¤</SelectItem>
                <SelectItem value="exchange_transfer">ê±°ë˜ì†Œ ì´ë™</SelectItem>
                <SelectItem value="p2p_trade">P2P ê±°ë˜</SelectItem>
                <SelectItem value="cash_change">í˜„ê¸ˆ ì¦ê°</SelectItem>
                <SelectItem value="exchange">í™˜ì „</SelectItem>
                <SelectItem value="transfer">ì´ì²´</SelectItem>
