import { useState, useEffect } from 'react';

interface RealTimeRates {
  [key: string]: number;
}

interface CryptoRates {
  [coin: string]: {
    KRW?: number;
    USDT?: number;
  };
}

export function useExchangeRates() {
  const [realTimeRates, setRealTimeRates] = useState<RealTimeRates>({});
  const [cryptoRates, setCryptoRates] = useState<CryptoRates>({});
  const [isFetchingRates, setIsFetchingRates] = useState(true);

  useEffect(() => {
    const fetchAllRates = async () => {
      setIsFetchingRates(true);
      try {
        // Fetch Fiat Rates
        const fiatResponse = await fetch('https://open.er-api.com/v6/latest/USD');
        if (!fiatResponse.ok) throw new Error('Fiat API request failed');
        const fiatData = await fiatResponse.json();
        const rates = fiatData.rates;

        setRealTimeRates({
          'KRW-USD': 1 / rates.KRW,
          'USD-KRW': rates.KRW,
          'KRW-VND': rates.VND / rates.KRW,
          'VND-KRW': rates.KRW / rates.VND,
          'USD-VND': rates.VND,
          'VND-USD': 1 / rates.VND,
          'USDT-USD': 1,
          'USD-USDT': 1,
          'USDT-KRW': rates.KRW,
          'KRW-USDT': 1 / rates.KRW,
          'USDT-VND': rates.VND,
          'VND-USDT': 1 / rates.VND,
        });

        // Fetch Crypto Rates
        const processedCryptoRates: CryptoRates = {};

        // Bithumb APIëŠ” CORS ë¬¸ì œë¡œ ì¼ì‹œì ìœ¼ë¡œ ë¹„í™œì„±í™”
        // ê¸°ë³¸ê°’ìœ¼ë¡œ USDT ê°€ê²© ì„¤ì •
        processedCryptoRates['USDT'] = {
          KRW: rates.KRW, // USDì™€ ë™ì¼í•œ ê°€ê²©ìœ¼ë¡œ ì„¤ì •
          USDT: 1
        };

        // Binance APIëŠ” CORS ë¬¸ì œë¡œ ì¼ì‹œì ìœ¼ë¡œ ë¹„í™œì„±í™”
        // ì¶”í›„ ì„œë²„ í”„ë¡ì‹œë¥¼ í†µí•´ êµ¬í˜„ ì˜ˆì •

        setCryptoRates(processedCryptoRates);

      } catch (error) {
        console.error("í™˜ìœ¨ API í˜¸ì¶œ ì‹¤íŒ¨:", error);
        console.log("ê¸°ë³¸ í™˜ìœ¨ ê°’ ì„¤ì • ì¤‘...");
        // ì˜¤ë¥˜ ë°œìƒ ì‹œ ê¸°ë³¸ê°’ ì„¤ì •
        setRealTimeRates({
          'KRW-USD': 1 / 1350,
          'USD-KRW': 1350,
          'KRW-VND': 1 / 0.055, // ì•½ 18.18
          'VND-KRW': 0.055,
          'USD-VND': 24500,
          'VND-USD': 1 / 24500,
          'USDT-USD': 1,
          'USD-USDT': 1,
          'USDT-KRW': 1350,
          'KRW-USDT': 1 / 1350,
          'USDT-VND': 24500,
          'VND-USDT': 1 / 24500,
        });
        setCryptoRates({
          'USDT': { KRW: 1350, USDT: 1 },
          'BTC': { KRW: 95000000, USDT: 70000 }
        });
      } finally {
        setIsFetchingRates(false);
      }
    };

    fetchAllRates();

    // Refresh rates every 30 seconds
    const interval = setInterval(fetchAllRates, 30000);
    return () => clearInterval(interval);
  }, []);

  return { realTimeRates, cryptoRates, isFetchingRates };
}

