export const formatNumberWithCommas = (value: number | string | null | undefined, decimalPlaces?: number): string => {
  if (value === null || value === undefined || value === '') return '';
  const num = typeof value === 'number' ? value : parseFloat(value);
  if (isNaN(num)) return '';

  const fixedValue = decimalPlaces !== undefined ? num.toFixed(decimalPlaces) : num.toString();
  let [integer, decimal] = fixedValue.split('.');
  integer = integer.replace(/,/g, '').replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  return decimal !== undefined ? `${integer}.${decimal}` : integer;
};

export const parseNumberWithCommas = (value: string | number): number | string => {
  if (typeof value !== 'string') return value;
  const parsed = parseFloat(value.replace(/,/g, ''));
  return isNaN(parsed) ? '' : parsed;
};

export const handleNumericInput = (value: string, setter: (value: string) => void): void => {
  const sanitized = value.replace(/[^0-9]/g, '');
  setter(sanitized);
};

export const handleIntegerInput = (value: string): string => {
  // ìˆ«ìì™€ ì½¤ë§ˆë§Œ í—ˆìš©, ì†Œìˆ«ì  ì œê±°
  return value.replace(/[^0-9,]/g, '');
};

export const handleDecimalInput = (value: string, setter: (value: string) => void): void => {
  const sanitized = value.replace(/[^0-9.]/g, '');
  const parts = sanitized.split('.');
  if (parts.length > 2) {
    setter(parts[0] + '.' + parts.slice(1).join(''));
  } else {
    setter(sanitized);
  }
};

// í†µí™” í‘œì‹œ í•¨ìˆ˜ ì¶”ê°€
export const getCurrencyDisplayName = (currency: string): string => {
  switch (currency) {
    case 'USD':
      return 'ë‹¬ëŸ¬';
    case 'KRW':
      return 'ì›';
    case 'VND':
      return 'ë™';
    case 'USDT':
      return 'USDT';
    default:
      return currency;
  }
};

// ë§¤ìˆ˜/ë§¤ë„ ìë™ íŒë³„ í—¬í¼ í•¨ìˆ˜
export const determineTransactionRateType = (
  transactionType: string,
  fromCurrency: string,
  toCurrency: string
): 'buy' | 'sell' => {
  // í™˜ì „ìƒ ê´€ì ì—ì„œ ë§¤ìˆ˜/ë§¤ë„ íŒë³„
  // ë§¤ìˆ˜ (myBuyRate): ê³ ê°ì´ í™˜ì „ìƒì—ê²Œ íŒë§¤ (í™˜ì „ìƒì´ ì™¸í™”ë¥¼ ë§¤ìˆ˜)
  // ë§¤ë„ (mySellRate): í™˜ì „ìƒì´ ê³ ê°ì—ê²Œ íŒë§¤ (í™˜ì „ìƒì´ ì™¸í™”ë¥¼ ë§¤ë„)

  switch (transactionType) {
    case 'cash_exchange':
      // í˜„ê¸ˆ í™˜ì „ì˜ ê²½ìš° ì…ë ¥ í†µí™” ê¸°ì¤€ìœ¼ë¡œ íŒë³„
      if (fromCurrency === 'USD') {
        // ê³ ê°ì´ USDë¥¼ ê°€ì ¸ì˜¨ ê²½ìš° â†’ í™˜ì „ìƒì´ USD ë§¤ìˆ˜
        return 'buy';
      } else if (toCurrency === 'USD') {
        // ê³ ê°ì´ USDë¥¼ ìš”ì²­í•˜ëŠ” ê²½ìš° â†’ í™˜ì „ìƒì´ USD ë§¤ë„  
        return 'sell';
      }
      // VND â†” KRW í™˜ì „ì˜ ê²½ìš°
      else if (fromCurrency === 'VND' && toCurrency === 'KRW') {
        // ê³ ê°ì´ VNDë¥¼ KRWë¡œ í™˜ì „ â†’ í™˜ì „ìƒì´ VND ë§¤ìˆ˜ (KRW ê¸°ì¤€ìœ¼ë¡œëŠ” ë§¤ë„)
        return 'buy';
      } else if (fromCurrency === 'KRW' && toCurrency === 'VND') {
        // ê³ ê°ì´ KRWë¥¼ VNDë¡œ í™˜ì „ â†’ í™˜ì „ìƒì´ VND ë§¤ë„ (KRW ê¸°ì¤€ìœ¼ë¡œëŠ” ë§¤ìˆ˜)
        return 'sell';
      }
      break;

    case 'cash_to_krw_account':
    case 'cash_to_vnd_account':
      // í˜„ê¸ˆ â†’ ê³„ì¢Œ: ê³ ê°ì´ í˜„ê¸ˆì„ ì…ê¸ˆí•˜ë¯€ë¡œ í™˜ì „ìƒì´ ì™¸í™” ë§¤ìˆ˜
      return 'buy';

    case 'vnd_account_to_krw_account':
      // VNDê³„ì¢Œ â†’ KRWê³„ì¢Œ: VNDë¥¼ ë°›ê³  KRWë¡œ í™˜ì „í•˜ë¯€ë¡œ í™˜ì „ìƒì´ VND ë§¤ìˆ˜
      return 'buy';

    case 'krw_account_to_vnd_account':
      // KRWê³„ì¢Œ â†’ VNDê³„ì¢Œ: KRWë¥¼ ë°›ê³  VNDë¡œ í™˜ì „í•˜ë¯€ë¡œ í™˜ì „ìƒì´ VND ë§¤ë„
      return 'sell';

    default:
      // ê¸°ë³¸ê°’ì€ ë§¤ìˆ˜
      return 'buy';
  }

  // ê¸°ë³¸ê°’
  return 'buy';
};

// í™˜ìœ¨ ìŒ ê²°ì • í—¬í¼ í•¨ìˆ˜
export const getExchangeRatePair = (
  fromCurrency: string,
  toCurrency: string
): { fromCurrency: string; toCurrency: string } => {
  // USDê°€ í¬í•¨ëœ ê²½ìš° USDë¥¼ ê¸°ì¤€ìœ¼ë¡œ ì„¤ì •
  if (fromCurrency === 'USD' || toCurrency === 'USD') {
    if (fromCurrency === 'USD') {
      return { fromCurrency: 'USD', toCurrency };
    } else {
      return { fromCurrency: 'USD', toCurrency: fromCurrency };
    }
  }

  // VND â†” KRWì˜ ê²½ìš° KRWë¥¼ ê¸°ì¤€ìœ¼ë¡œ ì„¤ì •
  if ((fromCurrency === 'VND' && toCurrency === 'KRW') || 
      (fromCurrency === 'KRW' && toCurrency === 'VND')) {
    return { fromCurrency: 'KRW', toCurrency: 'VND' };
  }

  // ê¸°ë³¸ì ìœ¼ë¡œ ì…ë ¥ ìˆœì„œ ìœ ì§€
  return { fromCurrency, toCurrency };
};

// í™˜ì „ìƒ ì‹œì„¸ ì¡°íšŒ ë° ì ìš© í•¨ìˆ˜
export const getExchangeShopRate = async (
  transactionType: string,
  fromCurrency: string,
  toCurrency: string,
  denomination?: string
): Promise<{ rate: number; source: string } | null> => {
  try {
    // ë§¤ìˆ˜/ë§¤ë„ íƒ€ì… ìë™ íŒë³„
    const rateType = determineTransactionRateType(transactionType, fromCurrency, toCurrency);

    // í™˜ìœ¨ ìŒ ê²°ì •
    const ratePair = getExchangeRatePair(fromCurrency, toCurrency);

    // í™˜ì „ìƒ ì‹œì„¸ ì¡°íšŒ
    const response = await fetch('/api/exchange-rates');
    if (!response.ok) {
      console.error('í™˜ì „ìƒ ì‹œì„¸ ì¡°íšŒ ì‹¤íŒ¨:', response.statusText);
      return null;
    }

    const exchangeRates = await response.json();

    // í•´ë‹¹ í†µí™”ìŒê³¼ ê¶Œì¢…ì— ë§ëŠ” í™œì„± ì‹œì„¸ ì°¾ê¸°
    const matchingRate = exchangeRates.find((rate: any) => 
      rate.fromCurrency === ratePair.fromCurrency &&
      rate.toCurrency === ratePair.toCurrency &&
      rate.denomination === (denomination || '') &&
      rate.isActive === 'true'
    );

    if (!matchingRate) {
      console.log(`í™˜ì „ìƒ ì‹œì„¸ ì—†ìŒ: ${ratePair.fromCurrency}-${ratePair.toCurrency} ${denomination || ''}`);
      return null;
    }

    // ë§¤ìˆ˜/ë§¤ë„ì— ë”°ë¼ ì ì ˆí•œ í™˜ìœ¨ ì„ íƒ
    const rate = rateType === 'buy' 
      ? parseFloat(matchingRate.myBuyRate || '0')
      : parseFloat(matchingRate.mySellRate || '0');

    if (rate <= 0) {
      console.log(`ìœ íš¨í•˜ì§€ ì•Šì€ í™˜ìœ¨: ${rateType} rate = ${rate}`);
      return null;
    }

    // í™˜ìœ¨ ë°©í–¥ ì¡°ì • (í•„ìš”í•œ ê²½ìš° ì—­í™˜ìœ¨ ê³„ì‚°)
    let finalRate = rate;
    if (ratePair.fromCurrency !== fromCurrency) {
      // ì—­í™˜ìœ¨ ê³„ì‚°
      finalRate = 1 / rate;
    }

    console.log(`í™˜ì „ìƒ ì‹œì„¸ ì ìš©: ${fromCurrency}â†’${toCurrency} ${denomination || ''} (${rateType}) = ${finalRate}`);

    return {
      rate: finalRate,
      source: `í™˜ì „ìƒ ${rateType === 'buy' ? 'ë§¤ìˆ˜' : 'ë§¤ë„'}ì‹œì„¸ (${ratePair.fromCurrency}-${ratePair.toCurrency} ${denomination || ''})`
    };

  } catch (error) {
    console.error('í™˜ì „ìƒ ì‹œì„¸ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜:', error);
    return null;
  }
};

// ê¶Œì¢…ë³„ í™˜ìœ¨ ê°€ì¤‘í‰ê·  ê³„ì‚° í•¨ìˆ˜
export const calculateWeightedExchangeRate = async (
  transactionType: string,
  fromCurrency: string,
  toCurrency: string,
  denominationAmounts: Record<string, number>
): Promise<{ rate: number; source: string } | null> => {
  try {
    let totalAmount = 0;
    let weightedSum = 0;
    const rateDetails: string[] = [];

    for (const [denomination, amount] of Object.entries(denominationAmounts)) {
      if (amount && amount > 0) {
        const denominationValue = parseFloat(denomination);
        const totalValue = denominationValue * amount;

        // í•´ë‹¹ ê¶Œì¢…ì˜ í™˜ì „ìƒ ì‹œì„¸ ì¡°íšŒ
        const rateResult = await getExchangeShopRate(transactionType, fromCurrency, toCurrency, denomination);

        if (rateResult) {
          totalAmount += totalValue;
          weightedSum += rateResult.rate * totalValue;
          rateDetails.push(`${denomination}${getCurrencyDisplayName(fromCurrency)} Ã— ${amount}ì¥ = ${rateResult.rate}`);
        }
      }
    }

    if (totalAmount === 0) {
      return null;
    }

    const weightedRate = weightedSum / totalAmount;

    console.log('ê¶Œì¢…ë³„ ê°€ì¤‘í‰ê·  í™˜ìœ¨ ê³„ì‚°:', {
      denominationAmounts,
      totalAmount,
      weightedRate,
      rateDetails
    });

    return {
      rate: weightedRate,
      source: `ê¶Œì¢…ë³„ ê°€ì¤‘í‰ê·  í™˜ìœ¨ (${rateDetails.join(', ')})`
    };

  } catch (error) {
    console.error('ê¶Œì¢…ë³„ ê°€ì¤‘í‰ê·  í™˜ìœ¨ ê³„ì‚° ì¤‘ ì˜¤ë¥˜:', error);
    return null;
  }
};

export const formatInputWithCommas = (value: string): string => {
  if (!value) return '';

  // Remove all non-numeric characters except decimal point
  const numericValue = value.replace(/[^0-9.]/g, '');

  // Handle multiple decimal points by keeping only the first one
  const parts = numericValue.split('.');
  let cleanedValue = parts[0];
  if (parts.length > 1) {
    cleanedValue = parts[0] + '.' + parts.slice(1).join('');
  }

  // Split again for formatting
  const finalParts = cleanedValue.split('.');

  // Format the integer part with commas
  const integerPart = finalParts[0].replace(/\B(?=(\d{3})+(?!\d))/g, ',');

  // Return formatted value, preserve all decimal places
  if (finalParts[1] !== undefined) {
    return `${integerPart}.${finalParts[1]}`;
  }
  return integerPart;
};

export const parseCommaFormattedNumber = (value: string): number => {
  if (!value) return 0;
  const numericValue = value.replace(/,/g, '');
  const parsed = parseFloat(numericValue);
  return isNaN(parsed) ? 0 : parsed;
};

// ì•”í˜¸í™”íì¸ì§€ íŒë‹¨í•˜ëŠ” í•¨ìˆ˜
export const isCryptoCurrency = (currency: string, assetName?: string): boolean => {
  const cryptoCurrencies = ['USDT', 'BTC', 'ETH', 'ADA', 'USDC', 'BNB', 'XRP', 'DOGE', 'SOL', 'AVAX', 'MATIC', 'DOT'];

  // currencyê°€ ì•”í˜¸í™”íì´ê±°ë‚˜
  if (cryptoCurrencies.includes(currency.toUpperCase())) {
    return true;
  }

  // assetNameì— ì•”í˜¸í™”í ê´€ë ¨ í‚¤ì›Œë“œê°€ ìˆëŠ” ê²½ìš°
  if (assetName) {
    const cryptoKeywords = ['USDT', 'BTC', 'ETH', 'Bithumb', 'Binance', 'Upbit', 'ì½”ì¸', 'Coin'];
    return cryptoKeywords.some(keyword => assetName.includes(keyword));
  }

  return false;
};

export const formatCurrency = (amount: number | string, currency: string): string => {
  const num = typeof amount === 'string' ? parseFloat(amount) : amount;

  if (amount === null || amount === undefined || isNaN(num)) {
    return '0';
  }

  if (num === 0) {
    return '0';
  }

  // ë² íŠ¸ë‚¨ëˆ, ì›í™”, ë‹¬ëŸ¬ëŠ” ì†Œìˆ«ì  í‘œì‹œ ì•ˆí•¨ - ë°˜ì˜¬ë¦¼ìœ¼ë¡œ ë³€ê²½
  if (currency === 'VND' || currency === 'KRW' || currency === 'USD') {
    return Math.round(num).toLocaleString();
  }

  // ì½”ì¸ì€ ì†Œìˆ«ì  2ìë¦¬ê¹Œì§€ë§Œ
  return num.toFixed(2);
};

// ê±°ë˜ë‚´ì—­ìš© ê¸ˆì•¡ í¬ë§·íŒ… í•¨ìˆ˜ - ì €ì¥ëœ ë°ì´í„°ë¥¼ ê·¸ëŒ€ë¡œ í‘œì‹œ (ê³„ì‚° ì—†ìŒ)
export const formatTransactionAmount = (amount: number | string, currency?: string, assetName?: string): string => {
  const num = typeof amount === 'string' ? parseFloat(amount) : amount;

  if (amount === null || amount === undefined || isNaN(num)) {
    return '0';
  }

  if (num === 0) {
    return '0';
  }

  // ì•”í˜¸í™”íì¸ ê²½ìš° ì†Œìˆ«ì  2ìë¦¬ê¹Œì§€ í‘œì‹œ
  if (currency && isCryptoCurrency(currency, assetName)) {
    return num.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
  }

  // ì¼ë°˜ í†µí™”ëŠ” ì €ì¥ëœ ë°ì´í„° ê·¸ëŒ€ë¡œ í‘œì‹œ (Math.round ì œê±°)
  return Math.floor(num).toLocaleString();
};

// ê±°ë˜ ìœ í˜•ë³„ í™˜ìœ¨ í‘œì‹œ í¬ë§·íŒ… í•¨ìˆ˜
export const formatExchangeRateByTransaction = (rate: number, fromCurrency?: string, toCurrency?: string, transactionType?: string): string => {
  if (rate === null || rate === undefined || isNaN(rate)) return '0';

  // ê±°ë˜ ìœ í˜•ë³„ í†µí™” ì¶”ì¶œ ë¡œì§
  let actualFromCurrency = fromCurrency;
  let actualToCurrency = toCurrency;

  // ê±°ë˜ ìœ í˜•ì—ì„œ í†µí™” ì •ë³´ ì¶”ì¶œ
  if (!fromCurrency || !toCurrency) {
    if (transactionType === 'cash_exchange') {
      // í™˜ìœ¨ ê°’ìœ¼ë¡œ í†µí™” ìœ í˜• ì¶”ì •
      if (rate > 1000) {
        // USD â†’ VND (í° ê°’)
        actualFromCurrency = 'USD';
        actualToCurrency = 'VND';
      } else if (rate > 10) {
        // KRW â†’ VND (ì¤‘ê°„ ê°’)
        actualFromCurrency = 'KRW';
        actualToCurrency = 'VND';
      } else {
        // VND â†’ KRW (ì‘ì€ ê°’)
        actualFromCurrency = 'VND';
        actualToCurrency = 'KRW';
      }
    }
  }

  // KRW â†’ VND í™˜ìœ¨: ì†Œìˆ«ì  2ìë¦¬ í‘œì‹œ
  if (actualFromCurrency === 'KRW' && actualToCurrency === 'VND') {
    return rate.toLocaleString('ko-KR', { 
      minimumFractionDigits: 2,
      maximumFractionDigits: 2 
    });
  }

  // USD â†’ VND í™˜ìœ¨: ì •ìˆ˜ë§Œ í‘œì‹œ
  if (actualFromCurrency === 'USD' && actualToCurrency === 'VND') {
    return Math.round(rate).toLocaleString('ko-KR');
  }

  // USD/KRW í™˜ìœ¨ì€ ì •ìˆ˜ë¡œ í‘œì‹œ
  if ((actualFromCurrency === 'USD' && actualToCurrency === 'KRW') || 
      (actualFromCurrency === 'KRW' && actualToCurrency === 'USD')) {
    return Math.round(rate).toLocaleString('ko-KR');
  }

  // VND â†’ KRW í™˜ìœ¨ì€ ì†Œìˆ«ì  2ìë¦¬ê¹Œì§€ í‘œì‹œ  
  if (actualFromCurrency === 'VND' && actualToCurrency === 'KRW') {
    return rate.toLocaleString('ko-KR', { 
      minimumFractionDigits: 0,
      maximumFractionDigits: 2 
    });
  }

  // ê¸°ë³¸ê°’: ì†Œìˆ«ì  2ìë¦¬ê¹Œì§€
  return rate.toLocaleString('ko-KR', { 
    minimumFractionDigits: 0,
    maximumFractionDigits: 2 
  });
};

// ê¶Œì¢…ë³„ í‘œì‹œ í˜•ì‹ í¬ë§·íŒ…
export const formatDenomination = (denom: string, currency: string): string => {
  switch (currency) {
    case 'VND':
      switch (denom) {
        case '500000': return '50ë§Œë™';
        case '200000': return '20ë§Œë™';
        case '100000': return '10ë§Œë™';
        case '50000': return '5ë§Œë™';
        case '20000': return '2ë§Œë™';
        case '10000': return '1ë§Œë™';
        case '5000': return '5ì²œë™';
        case '1000': return '1ì²œë™';
        default: return `${denom}ë™`;
      }
    case 'USD':
      switch (denom) {
        case '100': return '100ë‹¬ëŸ¬';
        case '50': return '50ë‹¬ëŸ¬';
        case '20_10': return '20/10ë‹¬ëŸ¬';
        case '5_2_1': return '5/2/1ë‹¬ëŸ¬';
        default: return `${denom}ë‹¬ëŸ¬`;
      }
    case 'KRW':
      switch (denom) {
        case '50000': return '5ë§Œì›';
        case '10000': return '1ë§Œì›';
        case '5000_1000': return '5ì²œ/1ì²œì›';
        default: return `${denom}ì›`;
      }
    default:
      return `${denom} ${currency}`;
  }
};

// í™˜ìœ¨ í‘œì‹œ í¬ë§·íŒ… (ë§¤ë§¤ì‹œì„¸ ê¸°ì¤€, í†µí™”ë³„ ì†Œìˆ«ì  ê·œì¹™ ì ìš©) - ê¸°ì¡´ í•¨ìˆ˜ ìœ ì§€
export const formatExchangeRate = (rate: number, fromCurrency?: string, toCurrency?: string): string => {
  if (rate === null || rate === undefined || isNaN(rate)) return '0';

  // USD/KRW í™˜ìœ¨ì€ ì •ìˆ˜ë¡œ í‘œì‹œ
  if ((fromCurrency === 'USD' && toCurrency === 'KRW') || 
      (fromCurrency === 'KRW' && toCurrency === 'USD')) {
    return Math.round(rate).toLocaleString('ko-KR');
  }

  // KRW/VND, VND/KRW í™˜ìœ¨ì€ ì†Œìˆ«ì  2ìë¦¬ê¹Œì§€ í‘œì‹œ  
  if ((fromCurrency === 'KRW' && toCurrency === 'VND') || 
      (fromCurrency === 'VND' && toCurrency === 'KRW')) {
    return rate.toLocaleString('ko-KR', { 
      minimumFractionDigits: 0,
      maximumFractionDigits: 2 
    });
  }

  // USD/VND, VND/USD í™˜ìœ¨ì€ ì •ìˆ˜ë¡œ í‘œì‹œ
  if ((fromCurrency === 'USD' && toCurrency === 'VND') || 
      (fromCurrency === 'VND' && toCurrency === 'USD')) {
    return Math.round(rate).toLocaleString('ko-KR');
  }

  // ê¸°ë³¸ê°’: ì†Œìˆ«ì  2ìë¦¬ê¹Œì§€
  return rate.toLocaleString('ko-KR', { 
    minimumFractionDigits: 0,
    maximumFractionDigits: 2 
  });
};

