import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { toast } from "@/hooks/use-toast";
import { Loader2, Eye, EyeOff, Key, TestTube, AlertCircle, CheckCircle, ArrowLeft, ChevronDown, ChevronRight } from "lucide-react";
import { Link } from "wouter";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";

interface BithumbApiKeys {
  connectKey: string;
  secretKey: string;
  api2Key: string;
  apiVersion: string;
}

interface TestResult {
  success: boolean;
  message: string;
}

interface ServiceConfig {
  [key: string]: {
    label: string;
    required: boolean;
    maxLength?: number;
    options?: string[];
  };
}

interface AllApiKeys {
  [service: string]: {
    [key: string]: string;
  };
}

interface ServicesResponse {
  services: string[];
  configs: {
    [service: string]: ServiceConfig;
  };
}

export default function ApiSettings() {
  const [selectedService, setSelectedService] = useState<string>("bithumb");
  const [expandedServices, setExpandedServices] = useState<Record<string, boolean>>({
    bithumb: true
  });
  const [formData, setFormData] = useState<Record<string, Record<string, string>>>({});
  const [showKeys, setShowKeys] = useState<Record<string, Record<string, boolean>>>({});

  const queryClient = useQueryClient();

  // ëª¨ë“  ì„œë¹„ìŠ¤ ì •ë³´ ì¡°íšŒ
  const { data: servicesData, isLoading: servicesLoading } = useQuery<ServicesResponse>({
    queryKey: ['/api/api-keys/services'],
  });

  // ëª¨ë“  API key ì¡°íšŒ
  const { data: allApiKeys, isLoading: keysLoading } = useQuery<AllApiKeys>({
    queryKey: ['/api/api-keys'],
  });

  // ì„œë¹„ìŠ¤ë³„ API key ì—…ë°ì´íŠ¸
  const updateMutation = useMutation({
    mutationFn: async ({ service, data }: { service: string; data: any }) => {
      const url = service === 'bithumb' ? '/api/bithumb/api-keys' : `/api/api-keys/${service}`;
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to update API keys');
      }
      return response.json();
    },
    onSuccess: (result, variables) => {
      toast({
        title: "ì„±ê³µ",
        description: result.message || "API Keyê°€ ì„±ê³µì ìœ¼ë¡œ ì—…ë°ì´íŠ¸ë˜ì—ˆìŠµë‹ˆë‹¤.",
      });
      setFormData(prev => ({ ...prev, [variables.service]: {} }));
      queryClient.invalidateQueries({ queryKey: ['/api/api-keys'] });
      if (variables.service === 'bithumb') {
        queryClient.invalidateQueries({ queryKey: ['/api/bithumb/api-keys'] });
      }
    },
    onError: (error: Error) => {
      toast({
        title: "ì˜¤ë¥˜",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  // API ì—°ê²° í…ŒìŠ¤íŠ¸
  const testMutation = useMutation({
    mutationFn: async (service: string) => {
      const url = service === 'bithumb' ? '/api/bithumb/test-connection' : `/api/api-keys/${service}/test`;
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });
      if (!response.ok) {
        throw new Error('Failed to test connection');
      }
      return response.json() as Promise<TestResult>;
    },
    onSuccess: (result) => {
      toast({
        title: result.success ? "ì—°ê²° ì„±ê³µ" : "ì—°ê²° ì‹¤íŒ¨",
        description: result.message,
        variant: result.success ? "default" : "destructive",
      });
    },
    onError: () => {
      toast({
        title: "ì˜¤ë¥˜",
        description: "API ì—°ê²° í…ŒìŠ¤íŠ¸ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.",
        variant: "destructive",
      });
    }
  });

  const handleSubmit = (service: string) => (e: React.FormEvent) => {
    e.preventDefault();

    const serviceFormData = formData[service] || {};
    const updates: any = {};

    Object.keys(serviceFormData).forEach(key => {
      if (serviceFormData[key] && serviceFormData[key].trim()) {
        updates[key] = serviceFormData[key].trim();
      }
    });

    if (Object.keys(updates).length === 0) {
      toast({
        title: "ì•Œë¦¼",
        description: "ë³€ê²½í•  API Keyë¥¼ ì…ë ¥í•˜ì„¸ìš”.",
        variant: "destructive",
      });
      return;
    }

    updateMutation.mutate({ service, data: updates });
  };

  const toggleShowKey = (service: string, keyType: string) => {
    setShowKeys(prev => ({
      ...prev,
      [service]: {
        ...prev[service],
        [keyType]: !prev[service]?.[keyType]
      }
    }));
  };

  const updateFormData = (service: string, key: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [service]: {
        ...prev[service],
        [key]: value
      }
    }));
  };

  const toggleServiceExpanded = (service: string) => {
    setExpandedServices(prev => ({
      ...prev,
      [service]: !prev[service]
    }));
  };

  const getServiceDisplayName = (service: string) => {
    const names: Record<string, string> = {
      bithumb: 'ë¹—ì¸ (Bithumb)',
      firebase: 'Firebase',
      binance: 'ë°”ì´ë‚¸ìŠ¤ (Binance)',
      coinGecko: 'CoinGecko',
      openExchangeRates: 'Open Exchange Rates'
    };
    return names[service] || service;
  };

  if (servicesLoading || keysLoading) {
    return (
      <div className="min-h-screen bg-background p-4 flex items-center justify-center">
        <Loader2 className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-4 space-y-6">
      <div className="max-w-2xl mx-auto">
        {/* í—¤ë” */}
        <div className="flex items-center gap-4 mb-6">
          <Link href="/">
            <Button variant="ghost" size="sm">
              <ArrowLeft className="h-4 w-4 mr-2" />
              ëŒ€ì‹œë³´ë“œë¡œ ëŒì•„ê°€ê¸°
            </Button>
          </Link>
        </div>

        <div className="flex items-center gap-3 mb-6">
          <Key className="h-6 w-6" />
          <h1 className="text-2xl font-bold">API í‚¤ ê´€ë¦¬</h1>
        </div>

        {/* ì„œë¹„ìŠ¤ë³„ API Key ê´€ë¦¬ */}
        <div className="space-y-4">
          {servicesData?.services.map((service) => {
            const serviceConfig = servicesData.configs[service];
            const serviceKeys = allApiKeys?.[service] || {};
            const isExpanded = expandedServices[service];

            return (
              <Card key={service}>
                <Collapsible 
                  open={isExpanded} 
                  onOpenChange={() => toggleServiceExpanded(service)}
                >
                  <CollapsibleTrigger asChild>
                    <CardHeader className="cursor-pointer hover:bg-muted/50 transition-colors">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          <Key className="h-5 w-5" />
                          <div>
                            <CardTitle className="text-lg">{getServiceDisplayName(service)}</CardTitle>
                            <CardDescription>
                              {service === 'bithumb' && `API ${serviceKeys.apiVersion || '1.0'} ë²„ì „`}
                              {service === 'firebase' && 'Firebase ì‹¤ì‹œê°„ í™˜ìœ¨ ì„œë¹„ìŠ¤'}
                              {service === 'binance' && 'Binance ê±°ë˜ì†Œ API'}
                              {service === 'coinGecko' && 'CoinGecko ê°€ê²© ë°ì´í„° API'}
                              {service === 'openExchangeRates' && 'í™˜ìœ¨ ë°ì´í„° API'}
                            </CardDescription>
                          </div>
                        </div>
                        {isExpanded ? <ChevronDown className="h-5 w-5" /> : <ChevronRight className="h-5 w-5" />}
                      </div>
                    </CardHeader>
                  </CollapsibleTrigger>

                  <CollapsibleContent>
                    <CardContent className="space-y-6">
                      {/* í˜„ì¬ ì„¤ì •ëœ í‚¤ í‘œì‹œ */}
                      <div className="space-y-3">
                        <h4 className="font-medium text-sm text-muted-foreground">í˜„ì¬ ì„¤ì •ëœ í‚¤</h4>
                        {Object.keys(serviceConfig).map((key) => {
                          const config = serviceConfig[key];
                          const currentValue = serviceKeys[key] || '';

                          if (key === 'apiVersion' && service === 'bithumb') {
                            return (
                              <div key={key}>
                                <Label>{config.label}</Label>
                                <div className="mt-1">
                                  <span className="text-sm font-mono bg-muted px-2 py-1 rounded">
                                    {currentValue || '1.0'}
                                  </span>
                                </div>
                              </div>
                            );
                          }

                          return (
                            <div key={key}>
                              <Label>{config.label}</Label>
                              <div className="flex items-center gap-2 mt-1">
                                <Input
                                  value={showKeys[service]?.[key] ? currentValue.replace(/\*/g, 'â—') : currentValue}
                                  readOnly
                                  className="font-mono text-sm"
                                  placeholder={currentValue ? "" : "ì„¤ì •ë˜ì§€ ì•ŠìŒ"}
                                />
                                {currentValue && (
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={() => toggleShowKey(service, key)}
                                  >
                                    {showKeys[service]?.[key] ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                  </Button>
                                )}
                              </div>
                            </div>
                          );
                        })}
                      </div>

                      {/* API ì—°ê²° í…ŒìŠ¤íŠ¸ */}
                      <Button
                        onClick={() => testMutation.mutate(service)}
                        disabled={testMutation.isPending}
                        variant="outline"
                        className="w-full"
                      >
                        {testMutation.isPending ? (
                          <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                        ) : (
                          <TestTube className="h-4 w-4 mr-2" />
                        )}
                        {getServiceDisplayName(service)} ì—°ê²° í…ŒìŠ¤íŠ¸
                      </Button>

                      {/* í‚¤ ë³€ê²½ í¼ */}
                      <div className="pt-4 border-t">
                        <h4 className="font-medium mb-4">í‚¤ ë³€ê²½</h4>
                        <form onSubmit={handleSubmit(service)} className="space-y-4">
                          {Object.keys(serviceConfig).map((key) => {
                            const config = serviceConfig[key];

                            if (key === 'apiVersion' && service === 'bithumb' && config.options) {
                              return (
                                <div key={key}>
                                  <Label htmlFor={`${service}-${key}`}>{config.label}</Label>
                                  <Select
                                    value={formData[service]?.[key] || ''}
                                    onValueChange={(value) => updateFormData(service, key, value)}
                                  >
                                    <SelectTrigger className="mt-1">
                                      <SelectValue placeholder={`${config.label} ì„ íƒ`} />
                                    </SelectTrigger>
                                    <SelectContent>
                                      {config.options.map((option) => (
                                        <SelectItem key={option} value={option}>
                                          API {option} ë²„ì „
                                        </SelectItem>
                                      ))}
                                    </SelectContent>
                                  </Select>
                                </div>
                              );
                            }

                            return (
                              <div key={key}>
                                <Label htmlFor={`${service}-${key}`}>
                                  {config.label} {config.maxLength && `(${config.maxLength}ìë¦¬)`}
                                </Label>
                                <Input
                                  id={`${service}-${key}`}
                                  type="text"
                                  placeholder={`ìƒˆë¡œìš´ ${config.label}ë¥¼ ì…ë ¥í•˜ì„¸ìš”`}
                                  value={formData[service]?.[key] || ''}
                                  onChange={(e) => updateFormData(service, key, e.target.value)}
                                  className="font-mono mt-1"
                                  maxLength={config.maxLength}
                                />
                                {config.maxLength && (
                                  <p className="text-sm text-muted-foreground mt-1">
                                    í˜„ì¬: {formData[service]?.[key]?.length || 0}/{config.maxLength}ì
                                  </p>
                                )}
                              </div>
                            );
                          })}

                          <Button
                            type="submit"
                            disabled={updateMutation.isPending}
                            className="w-full"
                          >
                            {updateMutation.isPending ? (
                              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                            ) : (
                              <CheckCircle className="h-4 w-4 mr-2" />
                            )}
                            {getServiceDisplayName(service)} API Key ì—…ë°ì´íŠ¸
                          </Button>
                        </form>
                      </div>
                    </CardContent>
                  </CollapsibleContent>
                </Collapsible>
              </Card>
            );
          })}
        </div>
      </div>
    </div>
  );
}

